import { computed, reactive, watchEffect } from 'vue';
import { usePreferredReducedMotion, useNetwork, useBattery, useMounted } from '@vueuse/core';

interface MeasureHandle {
  end: () => void;
}

export const useAnimationEnvironment = () => {
  const isMounted = useMounted();
  const prefersReducedMotion = usePreferredReducedMotion();
  const networkInfo = useNetwork();
  const batteryInfo = useBattery();

  const optimizations = reactive({
    reducedMotion: false,
    batteryOptimization: false,
    lazyLoading: true,
    imageOptimization: true,
    codesplitting: true,
    serviceWorker: false,
    memoryManagement: true,
  });

  watchEffect(() => {
    optimizations.reducedMotion = isMounted.value && prefersReducedMotion.value === 'reduce';

    const level = batteryInfo.level.value;
    optimizations.batteryOptimization = typeof level === 'number'
      ? level < 0.2 && !batteryInfo.charging.value
      : false;

    optimizations.serviceWorker = false;
  });

  const windowRef = computed(() => (isMounted.value ? window : null));

  const measureOperation = (_name?: string): MeasureHandle => {
    if (!isMounted.value || !windowRef.value) {
      return { end: () => {} };
    }

    const start = windowRef.value.performance.now();
    return {
      end: () => {
        if (!windowRef.value) return;
        windowRef.value.performance.now() - start;
      },
    };
  };

  return {
    optimizations,
    networkInfo,
    batteryInfo,
    prefersReducedMotion,
    windowRef,
    measureOperation,
  };
};

export type AnimationEnvironment = ReturnType<typeof useAnimationEnvironment>;

import { reactive, computed } from 'vue'
import { usePreferredReducedMotion } from '@vueuse/core'
import { useAnimationEnvironment } from './useAnimationEnvironment'

export interface ConductorState {
  isPlaying: boolean
  currentMode: 'rotation' | 'unison'
  cycleDuration: number
  providers: string[]
  currentChatIndex: number
  chatBubblePhase: 'hidden' | 'entering' | 'visible' | 'exiting'
  sparkSpeed: number
  pulseIntensity: number
}

export const useConductorState = () => {
  const state = reactive<ConductorState>({
    isPlaying: false,
    currentMode: 'rotation',
    cycleDuration: 7, // Match AiConductorGraphicV2 default
    providers: ['instagram', 'facebook', 'threads', 'linkedin', 'tiktok', 'youtube'],
    currentChatIndex: 0,
    chatBubblePhase: 'hidden',
    sparkSpeed: 0.9,
    pulseIntensity: 1.45
  })

  const prefersReducedMotion = usePreferredReducedMotion()
  const { optimizations } = useAnimationEnvironment()

  // Separate "can start" from "is playing" logic to fix circular dependency
  const canStartAnimation = computed(() =>
    prefersReducedMotion.value !== 'reduce' && !optimizations.reducedMotion
  )

  const shouldAnimate = computed(() =>
    canStartAnimation.value && state.isPlaying
  )

  return {
    state,
    canStartAnimation,
    shouldAnimate,
    prefersReducedMotion,
    optimizations
  }
}

