<script setup lang="ts">
import { ref, computed, onMounted, onUnmounted, watch } from 'vue'
import { useRafFn, useElementVisibility } from '@vueuse/core'
import { useAnimationBus } from '@/components/composables/useAnimationBus'
import { useConductorState } from '@/components/composables/useConductorState'

interface Props {
  cycleDuration?: number
  mode?: 'rotation' | 'unison'
}

const props = withDefaults(defineProps<Props>(), {
  cycleDuration: 7,
  mode: 'rotation'
})

const orchestratorRef = ref<HTMLElement>()
const bus = useAnimationBus()
const { state, canStartAnimation } = useConductorState()

// Viewport detection for performance
const isVisible = useElementVisibility(orchestratorRef)

// Animation timing state
let animationStartTime: number | null = null
let currentCycleStartTime: number | null = null
let animationFrameId: number | null = null

// Centralized animation loop
const animateLoop = (timestamp: DOMHighResTimeStamp) => {
  if (!animationStartTime) {
    animationStartTime = timestamp
    currentCycleStartTime = timestamp

    // Emit start event
    bus.emit('orchestrate-start', {
      mode: state.currentMode,
      cycleDuration: state.cycleDuration
    })
  }

  const elapsed = timestamp - currentCycleStartTime!
  const cycleDurationMs = state.cycleDuration * 1000

  // Calculate progress (0 to 1)
  const progress = elapsed / cycleDurationMs

  // Example: Activate components at specific intervals
  if (state.currentMode === 'rotation') {
    const componentCount = 6
    const activationInterval = cycleDurationMs / componentCount

    for (let i = 0; i < componentCount; i++) {
      const activationTime = i * activationInterval
      if (elapsed >= activationTime && elapsed < activationTime + 100) {
        bus.emit('pathway-activate', {
          index: i,
          provider: `component-${i}`,
          delay: 0
        })
      }
    }
  } else {
    // Unison mode - activate all at once
    if (elapsed < 100) {
      for (let i = 0; i < 6; i++) {
        bus.emit('pathway-activate', {
          index: i,
          provider: `component-${i}`,
          delay: 0
        })
      }
    }
  }

  // Check if cycle complete
  if (elapsed >= cycleDurationMs) {
    currentCycleStartTime = timestamp
    bus.emit('animation-complete', {
      component: 'orchestrator',
      type: 'enter'
    })
  }

  // Continue loop
  if (state.isPlaying && isVisible.value) {
    animationFrameId = requestAnimationFrame(animateLoop)
  }
}

// Start animation
const startAnimation = () => {
  if (!canStartAnimation.value) return

  state.isPlaying = true
  animationFrameId = requestAnimationFrame(animateLoop)
}

// Stop animation
const stopAnimation = () => {
  state.isPlaying = false

  if (animationFrameId) {
    cancelAnimationFrame(animationFrameId)
    animationFrameId = null
  }

  animationStartTime = null
  currentCycleStartTime = null

  bus.emit('stop-all')
}

// Auto-start when visible
watch(isVisible, (visible) => {
  if (visible && canStartAnimation.value && !state.isPlaying) {
    startAnimation()
  } else if (!visible && state.isPlaying) {
    stopAnimation()
  }
})

// Sync props to state
watch(() => props.mode, (mode) => { state.currentMode = mode }, { immediate: true })
watch(() => props.cycleDuration, (duration) => { state.cycleDuration = duration }, { immediate: true })

onMounted(() => {
  if (isVisible.value && canStartAnimation.value) {
    startAnimation()
  }
})

onUnmounted(() => {
  stopAnimation()
  bus.cleanup()
})

defineExpose({
  startAnimation,
  stopAnimation,
  isPlaying: computed(() => state.isPlaying)
})
</script>
```

