  const progress = elapsed / cycleDurationMs

  // Example: Activate components at specific intervals
  if (state.currentMode === 'rotation') {
    const componentCount = 6
    const activationInterval = cycleDurationMs / componentCount

    for (let i = 0; i < componentCount; i++) {
      const activationTime = i * activationInterval
      if (elapsed >= activationTime && elapsed < activationTime + 100) {
        bus.emit('pathway-activate', {
          index: i,
          provider: `component-${i}`,
          delay: 0
        })
      }
    }
  } else {
    // Unison mode - activate all at once
    if (elapsed < 100) {
      for (let i = 0; i < 6; i++) {
        bus.emit('pathway-activate', {
          index: i,
          provider: `component-${i}`,
          delay: 0
        })
      }
    }
  }

  // Check if cycle complete
  if (elapsed >= cycleDurationMs) {
    currentCycleStartTime = timestamp
    bus.emit('animation-complete', {
      component: 'orchestrator',
      type: 'enter'
    })
  }

  // Continue loop
  if (state.isPlaying && isVisible.value) {
    animationFrameId = requestAnimationFrame(animateLoop)
  }
}

// Start animation
const startAnimation = () => {
  if (!canStartAnimation.value) return

  state.isPlaying = true
  animationFrameId = requestAnimationFrame(animateLoop)
}

// Stop animation
const stopAnimation = () => {
  state.isPlaying = false

  if (animationFrameId) {
    cancelAnimationFrame(animationFrameId)
    animationFrameId = null
  }

  animationStartTime = null
  currentCycleStartTime = null

  bus.emit('stop-all')
}

// Auto-start when visible
watch(isVisible, (visible) => {
  if (visible && canStartAnimation.value && !state.isPlaying) {
    startAnimation()
  } else if (!visible && state.isPlaying) {
    stopAnimation()
  }
})

// Sync props to state
watch(() => props.mode, (mode) => { state.currentMode = mode }, { immediate: true })
watch(() => props.cycleDuration, (duration) => { state.cycleDuration = duration }, { immediate: true })

onMounted(() => {
  if (isVisible.value && canStartAnimation.value) {
    startAnimation()
  }
})

onUnmounted(() => {
  stopAnimation()
  bus.cleanup()
})

defineExpose({
  startAnimation,
  stopAnimation,
  isPlaying: computed(() => state.isPlaying)
})
</script>
```

### Step 2: Create Listening Child Components

```vue
<template>
  <div
    ref="childRef"
    class="child-component"
    :class="{ 'is-active': isActive }"
  >
    <!-- Component content -->
  </div>
</template>

<script setup lang="ts">
import { ref, onMounted, onUnmounted } from 'vue'
import { useAnimate } from '@vueuse/core'
import { useAnimationBus } from '@/components/composables/useAnimationBus'

interface Props {
  index: number
  provider: string
}

const props = defineProps<Props>()

const childRef = ref<HTMLElement>()
const isActive = ref(false)
const bus = useAnimationBus()

// Setup animation
const { play, cancel } = useAnimate(
  childRef,
  [
    { transform: 'scale(1)', opacity: 0.8 },
    { transform: 'scale(1.2)', opacity: 1 },
    { transform: 'scale(1)', opacity: 1 }
  ],
  {
    duration: 600,
    easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
    fill: 'forwards'
  }
)

// Listen for activation
const cleanupActivate = bus.on('pathway-activate', (data) => {
  if (data.index === props.index) {
    isActive.value = true
    play()

    // Reset after animation
    setTimeout(() => {
      isActive.value = false
    }, 600)
  }
})

// Listen for stop all
const cleanupStop = bus.on('stop-all', () => {
  isActive.value = false
  cancel()
})

onUnmounted(() => {
  cleanupActivate()
  cleanupStop()
})
</script>

<style scoped>
.child-component {
  transition: all 0.3s ease;
}

.child-component.is-active {
  /* Active state styles */
}
</style>
```

### Step 3: Wrap in AnimatedFloatingElement (Optional)

If the entire coordinated animation should float/appear:

