        notes: formattedNotes,
        projects: [SUPPORT_PROJECT_GID],
        assignee: CATEGORY_ASSIGNEES[ticketData.category] || null,
        custom_fields: {
          [CATEGORY_FIELD_GID]: CATEGORY_OPTIONS[ticketData.category],
          [PRIORITY_FIELD_GID]: PRIORITY_OPTIONS[ticketData.priority] || PRIORITY_OPTIONS['normal'],
          [CUSTOMER_EMAIL_FIELD_GID]: ticketData.customerEmail
        },
        tags: ['support', `category-${ticketData.category}`],
        // Set due date based on priority
        due_on: calculateSupportDueDate(ticketData.priority)
      }
    };

    // Create task
    const result = await tasksApiInstance.createTask(taskData, {});
    console.log('âœ… Support ticket created:', result.data.gid);

    // Store mapping between your ticket ID and Asana task GID
    await storeMappingInDatabase(ticketData.ticketId, result.data.gid);

    // Set up webhook to listen for updates to this task
    await setupTaskWebhook(result.data.gid, ticketData.ticketId);

    return {
      asanaTaskGid: result.data.gid,
      asanaTaskUrl: result.data.permalink_url,
      ticketId: ticketData.ticketId
    };

  } catch (error) {
    console.error('âŒ Failed to create support ticket:', error.message);
    throw error;
  }
}

/**
 * Calculate support ticket due date based on priority
 */
function calculateSupportDueDate(priority) {
  const today = new Date();
  const hoursToAdd = {
    'urgent': 4,    // Urgent: respond within 4 hours
    'high': 24,     // High: respond within 24 hours
    'normal': 48,   // Normal: respond within 2 business days
    'low': 72       // Low: respond within 3 business days
  };

  const hours = hoursToAdd[priority] || 48;
  const dueDate = new Date(today.getTime() + (hours * 60 * 60 * 1000));

  return dueDate.toISOString().split('T')[0];
}

