function formatDailySummary(bugs, tickets, date) {
  const dateStr = date.toLocaleDateString();

  // Group bugs by severity
  const bugsBySeverity = bugs.reduce((acc, bug) => {
    const severityField = bug.custom_fields?.find(f => f.name === 'Severity');
    const severity = severityField?.display_value || 'Unknown';
    acc[severity] = (acc[severity] || 0) + 1;
