#### Example 3: Set Up Webhook for Task Updates

**Scenario:** Listen for when support agents mark tasks as complete or add comments in Asana, then sync those updates back to your application and notify customers.

**Node.js Express Server Implementation:**
```javascript
const express = require('express');
const crypto = require('crypto');
const Asana = require('asana');

const app = express();
app.use(express.json());

// Initialize Asana client
const client = Asana.ApiClient.instance;
const token = client.authentications['token'];
token.accessToken = process.env.ASANA_ACCESS_TOKEN;

// Store webhook secrets (in production, use a database)
const webhookSecrets = new Map();

/**
 * Webhook establishment endpoint
 * Asana sends handshake request here when webhook is created
 */
app.post('/webhooks/asana/support', (req, res) => {
  const hookSecret = req.headers['x-hook-secret'];

  // Handshake: Asana sends secret, we echo it back
  if (hookSecret) {
    console.log('ðŸ¤ Webhook handshake received');

    // Store the secret for future verification
    webhookSecrets.set(req.body.webhook?.gid, hookSecret);

    // Respond with the same secret to complete handshake
    res.setHeader('X-Hook-Secret', hookSecret);
    res.status(200).send();

    console.log('âœ… Webhook handshake completed');
    return;
  }

  // Verify webhook signature for security
  const signature = req.headers['x-hook-signature'];
  const webhookGid = req.body.webhook?.gid;
  const secret = webhookSecrets.get(webhookGid);

  if (!verifyWebhookSignature(req.body, signature, secret)) {
    console.error('âŒ Invalid webhook signature');
    return res.status(401).send('Invalid signature');
  }

  // Process webhook events
  const events = req.body.events || [];
  console.log(`ðŸ“¥ Received ${events.length} webhook events`);

  // Process events asynchronously
  processWebhookEvents(events).catch(error => {
    console.error('Error processing events:', error);
  });

  // Respond immediately (Asana requires response within 10 seconds)
  res.status(200).send();
});

/**
 * Verify webhook signature using HMAC-SHA256
 * @param {Object} body - Request body
 * @param {string} signature - X-Hook-Signature header
 * @param {string} secret - Stored webhook secret
 * @returns {boolean} True if signature is valid
 */
function verifyWebhookSignature(body, signature, secret) {
  if (!signature || !secret) return false;

  const hmac = crypto.createHmac('sha256', secret);
  hmac.update(JSON.stringify(body));
  const calculatedSignature = hmac.digest('hex');

  // Use timing-safe comparison
  return crypto.timingSafeEqual(
    Buffer.from(signature),
    Buffer.from(calculatedSignature)
  );
}

/**
 * Process webhook events
 * @param {Array} events - Array of webhook events
 */
async function processWebhookEvents(events) {
  const tasksApiInstance = new Asana.TasksApi();
  const storiesApiInstance = new Asana.StoriesApi();

  for (const event of events) {
    try {
      console.log(`Processing event: ${event.action} on ${event.resource.resource_type}`);

      // Handle task completion
      if (event.resource.resource_type === 'task' &&
          event.action === 'changed') {

        // Fetch full task details (webhook events are "compact")
        const task = await tasksApiInstance.getTask(event.resource.gid, {
          opt_fields: 'name,completed,custom_fields,assignee.name'
        });

        // Check if task was marked as complete
        if (task.data.completed) {
          await handleTaskCompleted(task.data);
        }
      }

      // Handle new comments (stories)
      if (event.resource.resource_type === 'story' &&
          event.action === 'added') {

        // Fetch the comment details
        const story = await storiesApiInstance.getStory(event.resource.gid, {
          opt_fields: 'text,created_by.name,created_at'
        });

        // Only process comment-type stories (not system events)
        if (story.data.type === 'comment') {
          await handleNewComment(event.parent.gid, story.data);
        }
      }

    } catch (error) {
      console.error(`Error processing event ${event.resource.gid}:`, error.message);
    }
  }
}

