async function checkSLAEscalations() {
  const now = Date.now();

  // SLA timeframes in hours
  const SLA_LIMITS = {
    'Urgent': 4,
    'High': 24,
    'Normal': 48,
    'Low': 72
  };

  // Fetch open tickets
  const tickets = await tasksApi.getTasks({
    project: SUPPORT_PROJECT_GID,
    completed_since: 'now',
    opt_fields: 'name,created_at,custom_fields,assignee'
  });

  for (const ticket of tickets.data) {
    const createdAt = new Date(ticket.created_at).getTime();
    const ageHours = (now - createdAt) / (1000 * 60 * 60);

    const priorityField = ticket.custom_fields.find(f => f.name === 'Priority');
    const priority = priorityField?.display_value || 'Normal';
    const slaLimit = SLA_LIMITS[priority];

    if (ageHours > slaLimit && !ticket.assignee) {
      // Escalate!
      await escalateTicket(ticket.gid, ageHours, slaLimit);
    }
  }
}

async function escalateTicket(taskGid, ageHours, slaLimit) {
  const storiesApi = new Asana.StoriesApi();

  // Add escalation comment
  await storiesApi.createStoryForTask(taskGid, {
    data: {
      text: `âš ï¸ **SLA VIOLATION** - This ticket has been open for ${Math.round(ageHours)} hours, exceeding the ${slaLimit} hour SLA. Escalating to support manager.`
    }
  });

  // Update task
  await tasksApi.updateTask(taskGid, {
    data: {
      assignee: SUPPORT_MANAGER_GID,
      custom_fields: {
        [PRIORITY_FIELD_GID]: URGENT_OPTION_GID
      }
    }
  });

  console.log(`ðŸš¨ Escalated ticket ${taskGid}`);
}
