    35→<script setup lang="ts">
    36→import { computed, ref, onMounted, onUnmounted } from 'vue';
    37→import type { CollectionEntry } from 'astro:content';
    38→import { Icon } from '@iconify/vue';
    39→import DocSection from './DocSection.vue';
    40→import type { docCategories } from '@/content/docs/categories';
    41→
    42→const props = defineProps<{
    43→  docSlug?: string; // e.g., "getting-started/welcome"
    44→  docs: CollectionEntry<'docs'>[];
    45→  categories: typeof docCategories;
    46→}>();
    47→
    48→const activeCategoryId = ref(props.categories[0]?.id);
    49→const singleDoc = computed(() => !!props.docSlug);
    50→
    51→const currentDoc = computed(() => {
    52→  if (!props.docSlug) return null;
    53→  return props.docs.find(doc => doc.slug === props.docSlug);
    54→});
    55→
    56→const filteredDocs = computed(() => {
    57→  return props.docs
    58→    .filter(doc => doc.data.category === activeCategoryId.value)
    59→    .sort((a, b) => (a.data.order || 0) - (b.data.order || 0));
    60→});
    61→
    62→// Listen for category changes from sidebar
    63→const handleCategoryChange = (event: CustomEvent) => {
    64→  activeCategoryId.value = event.detail.category;
    65→};
    66→
    67→onMounted(() => {
    68→  window.addEventListener('category-change', handleCategoryChange as EventListener);
    69→});
    70→
    71→onUnmounted(() => {
    72→  window.removeEventListener('category-change', handleCategoryChange as EventListener);
    73→});
    74→</script>
