marked.use({
  headerIds: true,  // Enable automatic heading IDs for TOC
  mangle: false,    // Don't mangle IDs (keep them readable)
  breaks: true,     // GitHub-flavored line breaks
  gfm: true,        // GitHub Flavored Markdown
  renderer: {
    link(href, title, text) {
      // Add security attributes to external links
      const hrefStr = String(href || '');
      const titleStr = title ? String(title) : '';
      const textStr = String(text || '');

      if (hrefStr && /^https?:\/\//i.test(hrefStr)) {
        return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''} target="_blank" rel="noopener noreferrer">${textStr}</a>`;
      }
      return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''}>${textStr}</a>`;
    },
    blockquote(quote: string) {
      // Check for callout syntax: > [!TYPE] or > **TYPE:**
      const quoteStr = String(quote || '');
      const calloutMatch = quoteStr.match(/^<p>\[!(NOTE|INFO|WARNING|DANGER|SUCCESS|TIP)\](.*?)<\/p>/is);
      const altCalloutMatch = quoteStr.match(/^<p><strong>(NOTE|INFO|WARNING|DANGER|SUCCESS|TIP):<\/strong>(.*?)<\/p>/is);

      if (calloutMatch) {
        const [, type, content] = calloutMatch;
        const typeStr = type.toLowerCase();
        const contentStr = content.trim();
        return `<div class="callout callout-${typeStr}" data-callout="${typeStr}">${contentStr}</div>`;
      } else if (altCalloutMatch) {
        const [, type, content] = altCalloutMatch;
        const typeStr = type.toLowerCase();
        const contentStr = content.trim();
        return `<div class="callout callout-${typeStr}" data-callout="${typeStr}">${contentStr}</div>`;
      }

      // Default blockquote styling (already styled by prose)
      return `<blockquote>${quoteStr}</blockquote>`;
    },
    heading(text: string, level: number, raw: string) {
      // Generate clean IDs for headings (for TOC)
      const textStr = String(text || '');
      const rawStr = String(raw || '');
      const escapedText = rawStr.toLowerCase().replace(/[^\w]+/g, '-');
      return `<h${level} id="${escapedText}">${textStr}</h${level}>`;
    },
  }
});

