      clearSelectedUserTeam();
      clearCurrentUserMemberships();
      clearBilling();
      clearBillingBonus();
      clearAllTiktokCreatorInfo();
      clearOAuthCache();
      clearBillingCache();
      clearMediaCache();
      clearAllAnalyticsStores();

      // Then call the API to logout
      await logoutUser();

      // Wait briefly to ensure state is cleared
      await new Promise((resolve) => setTimeout(resolve, 100));

      return true;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Logout failed";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const refreshUserCache = async () => {
    const user = await currentUser();
    if (!user) {
      return null;
    }
    setCurrentUser(user);

    // Trigger realtime connection when user is refreshed/set
    setTimeout(() => {
      triggerRealtimeConnectionIfAvailable();
    }, 200);

    return user;
  };

  const numConnectedAccounts = async () => {
    if (!user.value) {
      return 0;
    }
    const oauthAccounts = await listOAuthAccounts([
      Query.equal("userId", user.value?.$id!),
      Query.equal("active", true),
    ]);
    return oauthAccounts.length;
  };

  const getCurrentUser = async () => {
    if (!mounted.value) {
      return user.value;
    }
    isLoading.value = true;
    error.value = null;
    try {
      // First ensure client session is properly initialized
      await ensureClientSession();

      const currentUserData = await currentUser();
      return currentUserData;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to get user";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const getJwt = async () => {
    const now = Date.now();
    const cache = jwtCache.get();

    // If we're within cache duration and not currently requesting, just call getUserJwt()
    if (now - cache.lastRequestTime < 15000) {
      // 15 seconds
      return getUserJwt();
    }

    // If another request is in progress, wait a bit and try again
    if (cache.isRequesting) {
      await new Promise((resolve) => setTimeout(resolve, 100));
      return getJwt();
    }

    isLoading.value = true;
    error.value = null;

    try {
      // Mark as requesting and update timestamp
      jwtCache.set({
        ...cache,
        isRequesting: true,
        lastRequestTime: now,
      });

      const result = await getUserJwt();

      // Clear requesting flag
      jwtCache.set({
        ...jwtCache.get(),
        isRequesting: false,
      });

      return result;
    } catch (err) {
      // Clear requesting flag on error too
      jwtCache.set({
        ...jwtCache.get(),
        isRequesting: false,
      });
      error.value = err instanceof Error ? err.message : "Failed to get JWT";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const sendVerificationEmail = async () => {
    isLoading.value = true;
    error.value = null;
    try {
      const sent = await checkAndSendVerificationEmail();
      return sent;
    } catch (err) {
      error.value =
        err instanceof Error
          ? err.message
          : "Failed to send verification email";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const verifyEmail = async (userId: string, secret: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const verified = await verifyUserEmail(userId, secret);
      return verified;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to verify email";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const getOrCreateMember = async (userId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const member = await getOrCreateUserMember(userId);
      return member;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to get/create member";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  return {
    // State
    user,
    session,
    isAdmin,
    isLoading,
    error,
    isAuthenticated,
    isEmailVerified,

    // Methods
    authenticate,
    numConnectedAccounts,
    register,
    login,
    logout,
    getCurrentUser,
    refreshUserCache,
    getJwt,
    sendVerificationEmail,
    verifyEmail,
    getOrCreateMember,
  };
}

