import { ref, computed } from "vue";
import { useStore } from "@nanostores/vue";
import {
  curUser,
  curUserSession,
  clearCurUser,
  setCurrentUser,
  authenticateUser,
  getOrCreateUserMember,
  createUser,
  loginUser,
  currentUser,
  getUserJwt,
  jwtCache,
  checkAndSendVerificationEmail,
  verifyUserEmail,
  logoutUser,
  checkUserInitialization,
} from "@/stores/authStore";
import { Query } from "appwrite";
import { DateTime } from "luxon";
import { listOAuthAccounts } from "@/stores/oauthStore";
import { clearBilling } from "@/stores/billingStore";
import { clearBillingBonus } from "@/stores/billingBonusStore";
import { clearAllTiktokCreatorInfo } from "@/stores/providers/tiktokStore";
import {
  clearCurrentUserMemberships,
  clearSelectedUserTeam,
} from "@/stores/teamStore";
import { useMounted } from "@vueuse/core";
import { clearOAuthCache } from "@composables/useOAuth";
import { clearBillingCache } from "@composables/useBilling";
import { clearMediaCache } from "@composables/useMediaStore";
import { clearAllAnalyticsStores } from "@/stores/providers/analyticsStore";
import { clearDocsCache } from "@/stores/docsStore";
import { ensureClientSession } from "@/utils/sessionSync";
import { resetSessionForNewAccount } from "@/utils/sessionReset";

// Global realtime connection trigger
let triggerRealtimeConnection: (() => void) | null = null;

// Function to register realtime connection trigger
export const registerRealtimeConnectionTrigger = (trigger: () => void) => {
  triggerRealtimeConnection = trigger;
};

// Function to manually trigger realtime connection
export const triggerRealtimeConnectionIfAvailable = () => {
  if (triggerRealtimeConnection) {
    triggerRealtimeConnection();
  }
};

export function useAuth() {
  const user = useStore(curUser);
  const session = useStore(curUserSession);
  const mounted = useMounted();
  const isLoading = ref(false);
  const error = ref<string | null>(null);

  const isAuthenticated = computed(() => !!user.value);
  const isEmailVerified = computed(
    () => user.value?.emailVerification === true
  );

  const isAdmin = computed(() =>
    user.value?.labels.some((label) => label.toLowerCase().includes("admin"))
  );

  const authenticate = async (userId: string, tokenSecret: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const authenticatedUser = await authenticateUser(userId, tokenSecret);
      return authenticatedUser;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Authentication failed";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const register = async (name: string, email: string, password: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const success = await createUser(name, email, password);
      return success;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Registration failed";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const login = async (email: string, password: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      // Clear any lingering logout flags before login
      const { isLoggingOut, recentLogout } = await import('@/stores/authStore');
      isLoggingOut.set(false);
      recentLogout.set(0);

      // Don't reset session here - let loginUser handle it properly
      const session = await loginUser(email, password);

      // Wait for session to propagate to backend services
      await new Promise((resolve) => setTimeout(resolve, 500));

      // Refresh user cache to ensure state is current
      await refreshUserCache();

      // Trigger realtime connection after successful login
      setTimeout(() => {
        triggerRealtimeConnectionIfAvailable();
      }, 200);

      return session;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Login failed";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const logout = async () => {
    isLoading.value = true;
    error.value = null;
    try {
      // Set logout flags immediately
      const { isLoggingOut, recentLogout } = await import('@/stores/authStore');
      isLoggingOut.set(true);
      recentLogout.set(Date.now());

      // First clear all local state
      clearCurUser();

      // Clear all related stores in a specific order
      clearSelectedUserTeam();
      clearCurrentUserMemberships();
      clearBilling();
      clearBillingBonus();
      clearAllTiktokCreatorInfo();
      clearOAuthCache();
      clearBillingCache();
      clearMediaCache();
      clearAllAnalyticsStores();

      // Then call the API to logout
      await logoutUser();

      // Wait briefly to ensure state is cleared
      await new Promise((resolve) => setTimeout(resolve, 100));

      return true;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Logout failed";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const refreshUserCache = async () => {
    const user = await currentUser();
    if (!user) {
      return null;
    }
    setCurrentUser(user);

    // Trigger realtime connection when user is refreshed/set
    setTimeout(() => {
      triggerRealtimeConnectionIfAvailable();
    }, 200);

    return user;
  };

  const numConnectedAccounts = async () => {
    if (!user.value) {
      return 0;
    }
    const oauthAccounts = await listOAuthAccounts([
      Query.equal("userId", user.value?.$id!),
      Query.equal("active", true),
    ]);
    return oauthAccounts.length;
  };

  const getCurrentUser = async () => {
    if (!mounted.value) {
      return user.value;
    }
    isLoading.value = true;
    error.value = null;
    try {
      // First ensure client session is properly initialized
      await ensureClientSession();

      const currentUserData = await currentUser();
      return currentUserData;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to get user";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const getJwt = async () => {
    const now = Date.now();
    const cache = jwtCache.get();

    // If we're within cache duration and not currently requesting, just call getUserJwt()
    if (now - cache.lastRequestTime < 15000) {
      // 15 seconds
      return getUserJwt();
    }

    // If another request is in progress, wait a bit and try again
    if (cache.isRequesting) {
      await new Promise((resolve) => setTimeout(resolve, 100));
      return getJwt();
    }

    isLoading.value = true;
    error.value = null;

    try {
      // Mark as requesting and update timestamp
      jwtCache.set({
        ...cache,
        isRequesting: true,
        lastRequestTime: now,
      });

      const result = await getUserJwt();

      // Clear requesting flag
      jwtCache.set({
        ...jwtCache.get(),
        isRequesting: false,
      });

      return result;
    } catch (err) {
      // Clear requesting flag on error too
      jwtCache.set({
        ...jwtCache.get(),
        isRequesting: false,
      });
      error.value = err instanceof Error ? err.message : "Failed to get JWT";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const sendVerificationEmail = async () => {
    isLoading.value = true;
    error.value = null;
    try {
      const sent = await checkAndSendVerificationEmail();
      return sent;
    } catch (err) {
      error.value =
        err instanceof Error
          ? err.message
          : "Failed to send verification email";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const verifyEmail = async (userId: string, secret: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const verified = await verifyUserEmail(userId, secret);
      return verified;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to verify email";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const getOrCreateMember = async (userId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const member = await getOrCreateUserMember(userId);
      return member;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to get/create member";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  return {
    // State
    user,
    session,
    isAdmin,
    isLoading,
    error,
    isAuthenticated,
    isEmailVerified,

    // Methods
    authenticate,
    numConnectedAccounts,
    register,
    login,
    logout,
    getCurrentUser,
    refreshUserCache,
    getJwt,
    sendVerificationEmail,
    verifyEmail,
    getOrCreateMember,
  };
}
