   113→    { name: 'TikTok', icon: 'simple-icons:tiktok', color: 'text-gray-900 dark:text-white' }
   114→  ],
   115→  radius: 140
   116→});
   117→
   118→const trophyRef = ref<HTMLElement | null>(null);
