interface Props {
  platforms?: Platform[];
  radius?: number;
}

const props = withDefaults(defineProps<Props>(), {
  platforms: () => [
    { name: 'Instagram', icon: 'mdi:instagram', color: 'text-pink-600 dark:text-pink-400' },
    { name: 'Facebook', icon: 'mdi:facebook', color: 'text-blue-600 dark:text-blue-400' },
    { name: 'LinkedIn', icon: 'mdi:linkedin', color: 'text-blue-700 dark:text-blue-500' },
    { name: 'TikTok', icon: 'simple-icons:tiktok', color: 'text-gray-900 dark:text-white' }
  ],
  radius: 140
});

const trophyRef = ref<HTMLElement | null>(null);
const platformRefs = ref<(HTMLElement | null)[]>(Array(props.platforms.length).fill(null));
const checkRefs = ref<(HTMLElement | null)[]>(Array(props.platforms.length).fill(null));
const showRings = ref(false);
const showLines = ref(false);
const platformsSettled = ref<boolean[]>(Array(props.platforms.length).fill(false));

// Setup animation for trophy
const { animateIn: animateTrophyIn } = useAnimateElement(trophyRef, {
  preset: 'scaleUp',
  duration: 600
});

// Setup animations for all platforms and checks
const platformAnimations = props.platforms.map((_, index) => {
  return useAnimateElement(() => platformRefs.value[index], {
    preset: 'popIn',
    duration: 500
  });
});

const checkAnimations = props.platforms.map((_, index) => {
  return useAnimateElement(() => checkRefs.value[index], {
    preset: 'scaleUp',
    duration: 300
  });
});

// Helper to set platform refs
const setPlatformRef = (el: any, index: number) => {
  if (el) platformRefs.value[index] = el;
};

// Helper to set check refs
const setCheckRef = (el: any, index: number) => {
  if (el) checkRefs.value[index] = el;
};

// Calculate radial positions for platforms
const getPlatformPosition = (index: number) => {
  const angle = (index / props.platforms.length) * 2 * Math.PI - Math.PI / 2; // Start from top
  const x = Math.cos(angle) * props.radius;
  const y = Math.sin(angle) * props.radius;

  return {
    left: `calc(50% + ${x}px)`,
    top: `calc(50% + ${y}px)`,
    transform: 'translate(-50%, -50%)'
  };
};

// Calculate line end positions for SVG (matches platform positions)
const getLineEndX = (index: number): number => {
  const angle = (index / props.platforms.length) * 2 * Math.PI - Math.PI / 2;
  return 200 + Math.cos(angle) * props.radius;
};

const getLineEndY = (index: number): number => {
  const angle = (index / props.platforms.length) * 2 * Math.PI - Math.PI / 2;
  return 128 + Math.sin(angle) * props.radius;
};

