---
import Navbar from "@vue/global/Navbar.vue";
import Footer from "@vue/global/Footer.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import { SITE_DATA } from "@/constants/siteData";

interface Props {
  title: string;
  navVariant?: 'default' | 'landing';
  footerVariant?: 'default' | 'landing';
}

const siteData = SITE_DATA;

const { title } = Astro.props;
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />

    <!-- Inter font (industry standard for SaaS 2025) -->
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap" rel="stylesheet">

    <style is:global>
      html {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
        font-family: system-ui, sans-serif;
      }

      [data-theme="dark"] {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body>
    <Navbar client:load transition:persist variant={Astro.props.navVariant || 'default'}/>
    <slot />
    <Footer
      client:load
      transition:persist
      footerVariant={Astro.props.footerVariant || (Astro.props.navVariant === 'landing' ? 'landing' : 'default')}
    />
    <CookieGuard client:load transition:persist />
    <SessionInitializer client:load />

    <!-- Teleport Layer -->
    <div id="teleport-layer"></div>
  </body>
</html>

