/* Landing page typography utilities */

.heading-hero {
  @apply text-display-2xl font-extrabold leading-tight tracking-tight;
}

.heading-section {
  @apply text-display-xl font-bold leading-tight tracking-tight;
}

.heading-card {
  @apply text-3xl font-semibold leading-snug;
}

.body-hero {
  @apply text-xl lg:text-2xl leading-relaxed text-gray-600 dark:text-gray-300;
}

.body-large {
  @apply text-lg leading-relaxed text-gray-600 dark:text-gray-300;
}

.gradient-text-primary {
  background: linear-gradient(135deg, theme('colors.primary.500') 0%, theme('colors.accent.purple.500') 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.gradient-text-light {
  background: linear-gradient(135deg, white 0%, theme('colors.gray.300') 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

