            50: '#faf5ff',
            100: '#f3e8ff',
            200: '#e9d5ff',
            300: '#d8b4fe',
            400: '#c084fc',
            500: '#7c3aed',  // AI features
            600: '#6d28d9',
            700: '#5b21b6',
            800: '#4c1d95',
            900: '#3b0764',
          },
          orange: {
            50: '#fff7ed',
            100: '#ffedd5',
            200: '#fed7aa',
            300: '#fdba74',
            400: '#fb923c',
            500: '#f59e0b',  // CTAs/highlights
            600: '#d97706',
            700: '#b45309',
            800: '#92400e',
            900: '#78350f',
          },
        },

        // Dark mode optimized colors
        dark: {
          bg: {
            primary: '#0D1117',     // Near-black (not pure black)
            secondary: '#161B22',
            tertiary: '#1F2937',
          },
          border: '#30363d',
          text: {
            primary: '#f9fafb',
            secondary: '#d1d5db',
            tertiary: '#9ca3af',
          },
        },
      },

      // Fluid typography with clamp()
      fontSize: {
        'display-2xl': 'clamp(3rem, 5vw, 4.5rem)',    // 48-72px hero
        'display-xl': 'clamp(2.5rem, 4vw, 3.5rem)',   // 40-56px
