.heading-hero {
  @apply text-display-2xl font-extrabold leading-tight tracking-tight;
}

.heading-section {
  @apply text-display-xl font-bold leading-tight tracking-tight;
}

.heading-card {
  @apply text-3xl font-semibold leading-snug;
}

.body-hero {
  @apply text-xl lg:text-2xl leading-relaxed text-gray-600 dark:text-gray-300;
}

.body-large {
  @apply text-lg leading-relaxed text-gray-600 dark:text-gray-300;
}

.gradient-text-primary {
  background: linear-gradient(135deg, #007c89 0%, #7c3aed 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.gradient-text-light {
  background: linear-gradient(135deg, white 0%, #d1d5db 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

