  font-size: var(--font-size-display-2xl);
  font-weight: 800;
  line-height: 1.1;
  letter-spacing: -0.025em;
}

.heading-section {
  font-size: var(--font-size-display-xl);
  font-weight: 700;
  line-height: 1.1;
  letter-spacing: -0.025em;
}

.heading-card {
  font-size: var(--font-size-heading-card);
  font-weight: 600;
  line-height: 1.25;
}

.body-hero {
  font-size: 1.25rem;
  line-height: 1.75;
  color: rgb(75 85 99);
}

@media (min-width: 1024px) {
  .body-hero {
    font-size: 1.5rem;
  }
}

html[data-theme="dark"] .body-hero {
  color: rgb(209 213 219);
}

.body-large {
  font-size: 1.125rem;
  line-height: 1.75;
  color: rgb(75 85 99);
}

html[data-theme="dark"] .body-large {
  color: rgb(209 213 219);
}

