file.content:
  // AnimatedFloatingElement passthrough props
  preset?: 'fadeIn' | 'slideUp' | 'slideDown' | 'slideLeft' | 'slideRight' | 'scaleIn' | 'scaleOut'
  teleportTo?: string
  zIndex?: number
  positioning?: Partial<CSSProperties>
  skipInitialAnimation?: boolean
  closeOnClickOutside?: boolean
}

const props = withDefaults(defineProps<Props>(), {
  modelValue: true, // Explicitly default to visible
  providersDisplayed: () => ['instagram', 'facebook', 'threads', 'linkedin', 'tiktok', 'youtube'],
  cycleDuration: 7,
  sparkSpeed: 0.9,
  pulseIntensity: 1.45,
  mode: 'rotation',
  showConductorCommand: true,
  showProviderResponse: true,
  preset: 'fadeIn',
  teleportTo: '#hero-animation-layer',
  zIndex: 100,
  positioning: () => ({
    position: 'absolute',
    inset: 0,
    width: '100%',
    height: '100%'
  }),
  skipInitialAnimation: false,
  closeOnClickOutside: false
})

// Template refs
const conductorRef = ref<HTMLElement>()
const floatingElementRef = useTemplateRef('floatingElementRef')
const orbRef = ref<InstanceType<typeof AiOrb>>()
const pathwayRefs = ref<InstanceType<typeof SocialPathway>[]>([])
const bubbleRef = ref<InstanceType<typeof ChatBubble>>()

// Triple-layer viewport detection
const elementVisible = useElementVisibility(conductorRef, {
  threshold: 0.1, // Element must be 10% visible
  rootMargin: '0px 0px -40px 0px' // Standard format: top right bottom left
})
const documentVisible = useDocumentVisibility()
const currentPath = ref(typeof window !== 'undefined' ? window.location.pathname : '')

// Combined viewport readiness state (removed windowFocus - too aggressive)
const isViewportReady = computed(() =>
  elementVisible.value &&
  documentVisible.value === 'visible'

)

// v-model visibility control - with explicit default, we always use useVModel
const isVisible = useVModel(props, 'modelValue', emit)

// Set pathway refs with proper validation
const setPathwayRef = (el: any, index: number) => {
  if (el && index < pathwayRefs.value.length) {
    pathwayRefs.value[index] = el
  }
}

// Composables
const bus = useAnimationBus()
const { state, canStartAnimation, shouldAnimate, prefersReducedMotion, optimizations } = useConductorState()
const { measureOperation } = usePerformance()

// Component state
const isComponentReady = ref(false)
const chatBubbleIndex = ref(0)
const isFloatingAnimating = ref(false)
const hasScheduledRestart = ref(false)

// Throttled animation checks with RAF
const { pause: pauseRaf, resume: resumeRaf } = useRafFn(() => {
  if (!isViewportReady.value) {
    stopAnimation()
    hasScheduledRestart.value = false // Reset flag when not ready
  } else if (!state.isPlaying && isComponentReady.value && !isFloatingAnimating.value && !hasScheduledRestart.value) {
    // Only schedule once per transition to ready state
    hasScheduledRestart.value = true
    scheduleOrchestrationStart(100)
  }
}, { immediate: false })

// Provider response messages
const providerResponses: Record<string, string> = {
  instagram: 'Posted!',
  facebook: 'Shared!',
  threads: 'Threaded!',
  linkedin: 'Published!',
  tiktok: 'Posted!',
  youtube: 'Uploaded!',
  twitter: 'Tweeted!',
  pinterest: 'Pinned!',
  snapchat: 'Snapped!',
  discord: 'Sent!',
  telegram: 'Broadcast!',
  whatsapp: 'Sent!'
}

