// Orchestrate the animation sequence
onMounted(() => {
  // 1. Show trophy first
  setTimeout(() => {
    animateTrophyIn();
    setTimeout(() => {
      showRings.value = true;
    }, 300);
  }, 100);

  // 2. Show connecting lines
  setTimeout(() => {
    showLines.value = true;
  }, 400);

  // 3. Burst platforms outward with stagger
  props.platforms.forEach((_, index) => {
    setTimeout(() => {
      const platformEl = platformRefs.value[index];
      if (platformEl) {
        const { animateIn } = useAnimateElement(platformEl, {
          preset: 'popIn',
          duration: 500
        });
        animateIn();

        // Mark as settled after animation
        setTimeout(() => {
          platformsSettled.value[index] = true;
        }, 500);
      }
    }, 600 + index * 150);
  });

  // 4. Show checkmarks after platforms settle
  props.platforms.forEach((_, index) => {
    setTimeout(() => {
      const checkEl = checkRefs.value[index];
      if (checkEl) {
        const { animateIn } = useAnimateElement(checkEl, {
          preset: 'scaleUp',
          duration: 300
        });
        animateIn();
      }
    }, 1200 + index * 150);
  });
});
</script>

