### 2.1 Update `src/pages/index.astro`

**Current Issues:**
- Background gradients are hardcoded (not using design tokens)
- Section order doesn't match research findings
- Missing social proof bar
- Pricing should move to dedicated page

**Updated Structure:**

```astro
---
import Layout from "../layouts/Layout.astro";
import HeroSection from "@vue/views/home/HeroSection.vue";
import SocialProofBar from "@vue/views/home/SocialProofBar.vue"; // ⭐ NEW
import ValuePropositionSection from "@vue/views/home/ValuePropositionSection.vue"; // ⭐ NEW
import HowItWorksSection from "@vue/views/home/HowItWorksSection.vue";
import ProvidersSection from "@vue/views/home/ProvidersSection.vue";
import WorkflowsSection from "@vue/views/home/WorkflowsSection.vue";
import TestimonialsSection from "@vue/views/home/TestimonialsSection.vue";
import FaqSection from "@vue/views/home/FaqSection.vue";
import PricingTeaser from "@vue/views/home/PricingTeaser.vue"; // ⭐ NEW (teaser only)
import CTASection from "@vue/views/home/CTASection.vue";
import { ENABLED_PROVIDERS } from "@/constants/providerConstants";

const providers = ENABLED_PROVIDERS.filter(
  (provider) => provider !== "facebook_pages" && provider !== "google"
);
---

<Layout title="Home" navVariant="landing">
  <div class="landing-wrapper">
    <!-- ⭐ NEW: Updated background using design tokens -->
    <div class="landing-background" aria-hidden="true"></div>

    <main class="relative z-10">
      <!-- ⭐ UPDATED ORDER based on research -->
      <HeroSection client:load />
      <SocialProofBar client:load /> <!-- ⭐ NEW: Trust indicators -->
      <ValuePropositionSection client:load /> <!-- ⭐ NEW: 3-column value props -->
      <HowItWorksSection client:load />
      <ProvidersSection client:load providers={providers} />
      <WorkflowsSection client:load />
      <TestimonialsSection client:load />
      <FaqSection client:load />
      <PricingTeaser client:load /> <!-- ⭐ CHANGED: Teaser instead of full pricing -->
      <div id="cta">
        <CTASection client:load />
      </div>

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: ["class", '[data-theme="dark"]'],
  content: ["./src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}"],
  theme: {
    extend: {
      colors: {
        primary: {
          100: "#cce5e7",
          200: "#99cbd0",
          300: "#66b0b8",
          400: "#3396a1",
          500: "#007c89",
          600: "#00636e",
          700: "#004a52",
          800: "#003237",
          900: "#00191b",
        },

        // Accent colors from research
        accent: {
          purple: {
            50: '#faf5ff',
            100: '#f3e8ff',
            200: '#e9d5ff',
            300: '#d8b4fe',
            400: '#c084fc',
            500: '#7c3aed',  // AI features
            600: '#6d28d9',
            700: '#5b21b6',
            800: '#4c1d95',
            900: '#3b0764',
          },
          orange: {
            50: '#fff7ed',
            100: '#ffedd5',
            200: '#fed7aa',
            300: '#fdba74',
            400: '#fb923c',
            500: '#f59e0b',  // CTAs/highlights
            600: '#d97706',
            700: '#b45309',
            800: '#92400e',
            900: '#78350f',
          },
        },

        // Dark mode optimized colors
        dark: {
          bg: {
            primary: '#0D1117',     // Near-black (not pure black)
            secondary: '#161B22',
            tertiary: '#1F2937',
          },
          border: '#30363d',
          text: {
            primary: '#f9fafb',
            secondary: '#d1d5db',
            tertiary: '#9ca3af',
          },
        },
      },

      // Fluid typography with clamp()
      fontSize: {
        'display-2xl': 'clamp(3rem, 5vw, 4.5rem)',    // 48-72px hero
        'display-xl': 'clamp(2.5rem, 4vw, 3.5rem)',   // 40-56px
        '3xl': 'clamp(1.875rem, 3vw, 2.25rem)',       // 30-36px
      },

      // Letter spacing for headlines
      letterSpacing: {
        tighter: '-0.05em',
        tight: '-0.025em',
      },

      // Animation durations - synced with src/constants/animations.ts
      transitionDuration: {
        'instant': '100ms',  // Quick feedback
        'fast': '200ms',     // Hover states, simple selections
        'normal': '300ms',   // Standard transitions
        'slow': '500ms',     // Page transitions (max)
        'celebration': '2000ms' // Success celebrations
      },

      // Animation timing functions - synced with src/constants/animations.ts
      transitionTimingFunction: {
        'ease-out-custom': 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'ease-in-out-custom': 'cubic-bezier(0.42, 0, 0.58, 1)',
        'bounce-custom': 'cubic-bezier(0.34, 1.56, 0.64, 1)',
        'smooth': 'cubic-bezier(0.16, 1, 0.3, 1)',
        'sharp': 'cubic-bezier(0.4, 0, 0.2, 1)',
        'elastic': 'cubic-bezier(0.68, -0.55, 0.265, 1.55)'
      },

      // Minimal utility animations (for static decorative elements only)
      // Use useAnimateElement composable for all interactive/controlled animations
      animation: {
        'fade-in': 'fadeIn 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'slide-in-up': 'slideInUp 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'pulse-slow': 'pulse 3s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'spin-slow': 'spin 8s linear infinite',
        'float-slow': 'floatSlow 20s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'shimmer': 'shimmer 2s linear infinite',
      },

      keyframes: {
        fadeIn: {
          '0%': { opacity: '0' },
          '100%': { opacity: '1' }
        },
        slideInUp: {
          '0%': { opacity: '0', transform: 'translateY(20px)' },
          '100%': { opacity: '1', transform: 'translateY(0)' }
        },
        floatSlow: {
          '0%, 100%': { transform: 'translate(0, 0)' },
          '50%': { transform: 'translate(20px, -20px)' }
        },
        shimmer: {
          '0%': { transform: 'translateX(-100%)' },
          '100%': { transform: 'translateX(100%)' }
        }
      }
    },
  },
  plugins: [
    require('@tailwindcss/typography'),
  ],
};

