   296→/* ===== KEYFRAME ANIMATIONS ===== */
   297→@keyframes fadeIn {
   298→  from {
   299→    opacity: 0;
   300→  }
   301→  to {
   302→    opacity: 1;
   303→  }
   304→}
   305→
   306→@keyframes fadeOut {
   307→  from {
   308→    opacity: 1;
   309→  }
   310→  to {
   311→    opacity: 0;
   312→  }
   313→}
   314→
   315→@keyframes slideInUp {
   316→  from {
   317→    opacity: 0;
   318→    transform: translateY(1rem);
   319→  }
   320→  to {
   321→    opacity: 1;
   322→    transform: translateY(0);
   323→  }
   324→}
   325→
   326→@keyframes slideInDown {
   327→  from {
   328→    opacity: 0;
