   330→  }
   331→  to {
   332→    opacity: 1;
   333→    transform: translateY(0);
   334→  }
   335→}
   336→
   337→@keyframes scaleIn {
   338→  from {
   339→    opacity: 0;
   340→    transform: scale(0.9);
   341→  }
   342→  to {
   343→    opacity: 1;
   344→    transform: scale(1);
   345→  }
   346→}
   347→
   348→@keyframes scaleOut {
   349→  from {
   350→    opacity: 1;
   351→    transform: scale(1);
   352→  }
   353→  to {
   354→    opacity: 0;
   355→    transform: scale(0.9);
   356→  }
   357→}
   358→
   359→@keyframes pulse {
   360→  0%, 100% {
   361→    opacity: 1;
   362→  }
   363→  50% {
   364→    opacity: 0.5;
   365→  }
   366→}
   367→
   368→@keyframes bounce {
   369→  0%, 100% {
