   611→  align-items: center;
   612→  padding: var(--spacing-1) var(--spacing-2);
   613→  font-size: var(--font-size-xs);
   614→  font-weight: var(--font-weight-medium);
   615→  line-height: var(--line-height-tight);
   616→  border-radius: var(--radius-base);
   617→  text-transform: uppercase;
   618→  letter-spacing: 0.025em;
   619→}
   620→
   621→/* Modal backdrop styles */
   622→.modal-backdrop {
   623→  position: fixed;
   624→  inset: 0;
   625→  background-color: rgb(0 0 0 / 0.5);
   626→  backdrop-filter: blur(4px);
   627→  -webkit-backdrop-filter: blur(4px);
   628→  z-index: var(--z-index-modal-backdrop);
   629→}
   630→
   631→[data-theme="dark"] .modal-backdrop {
   632→  background-color: rgb(0 0 0 / 0.7);
   633→}
   634→
   635→/* Responsive utilities */
   636→@media (max-width: 640px) {
   637→  .responsive-text {
   638→    font-size: calc(var(--font-size-base) * 0.9);
   639→  }
   640→  
   641→  .responsive-spacing {
   642→    padding: var(--spacing-4);
   643→  }
   644→}
   645→
   646→@media (min-width: 1024px) {
   647→  .responsive-text {
   648→    font-size: calc(var(--font-size-base) * 1.1);
   649→  }
   650→}
   651→
   652→/* ===== ACCESSIBILITY ENHANCEMENTS ===== */
   653→
   654→/* Reduced motion support */
   655→@media (prefers-reduced-motion: reduce) {
   656→  *,
   657→  *::before,
   658→  *::after {
   659→    animation-duration: 0.01ms !important;
   660→    animation-iteration-count: 1 !important;
   661→    transition-duration: 0.01ms !important;
   662→    scroll-behavior: auto !important;
   663→  }
   664→}
   665→
   666→/* High contrast mode support */
   667→@media (prefers-contrast: high) {
   668→  .form-control-base {
   669→    border-width: 2px;
   670→  }
   671→  
   672→  .btn-base {
   673→    border-width: 2px;
   674→  }
   675→}
   676→
   677→/* Focus visible improvements */
   678→@media (prefers-reduced-motion: no-preference) {
   679→  .focus-visible-enhance:focus-visible {
   680→    transform: scale(1.02);
   681→  }
   682→}
   683→
   684→/* ===== REAL-TIME & NOTIFICATION COMPONENTS ===== */
   685→
   686→/* Notification stack z-index */
   687→:root {
   688→  --z-index-notification: 1000;
   689→  --z-index-chat: 900;
   690→  --z-index-toast: 1100;
   691→  --z-index-modal: 1200;
   692→  --z-index-modal-backdrop: 1150;
   693→}
   694→
   695→/* Glass morphism effects */
   696→.glass-none {
   697→  background: var(--color-surface-primary);
   698→  border: 1px solid var(--color-border-primary);
   699→}
   700→
   701→.glass-subtle {
   702→  background: rgba(255, 255, 255, 0.95);
   703→  backdrop-filter: blur(10px);
   704→  -webkit-backdrop-filter: blur(10px);
   705→  border: 1px solid rgba(255, 255, 255, 0.2);
   706→}
   707→
   708→.glass-medium {
   709→  background: rgba(255, 255, 255, 0.85);
   710→  backdrop-filter: blur(20px);
   711→  -webkit-backdrop-filter: blur(20px);
   712→  border: 1px solid rgba(255, 255, 255, 0.3);
   713→}
   714→
   715→.glass-strong {
   716→  background: rgba(255, 255, 255, 0.75);
   717→  backdrop-filter: blur(30px);
   718→  -webkit-backdrop-filter: blur(30px);
   719→  border: 1px solid rgba(255, 255, 255, 0.4);
   720→}
   721→
   722→[data-theme="dark"] .glass-subtle {
   723→  background: rgba(17, 24, 39, 0.95);
   724→  border: 1px solid rgba(75, 85, 99, 0.2);
   725→}
   726→
   727→[data-theme="dark"] .glass-medium {
   728→  background: rgba(17, 24, 39, 0.85);
   729→  border: 1px solid rgba(75, 85, 99, 0.3);
   730→}
   731→
   732→[data-theme="dark"] .glass-strong {
   733→  background: rgba(17, 24, 39, 0.75);
   734→  border: 1px solid rgba(75, 85, 99, 0.4);
   735→}
   736→
   737→/* Screen reader only content */
   738→.sr-only {
   739→  position: absolute !important;
   740→  width: 1px !important;
   741→  height: 1px !important;
   742→  padding: 0 !important;
   743→  margin: -1px !important;
   744→  overflow: hidden !important;
   745→  clip: rect(0, 0, 0, 0) !important;
   746→  white-space: nowrap !important;
   747→  border: 0 !important;
   748→}
   749→
   750→/* Skip link for keyboard navigation */
   751→.skip-link {
   752→  position: absolute;
   753→  left: -10000px;
   754→  top: auto;
   755→  width: 1px;
   756→  height: 1px;
   757→  overflow: hidden;
   758→  z-index: 999999;
   759→  padding: var(--spacing-2) var(--spacing-4);
   760→  background: var(--color-primary-500);
   761→  color: white;
   762→  text-decoration: none;
   763→  border-radius: var(--radius-base);
   764→  font-weight: var(--font-weight-medium);
   765→}
   766→
   767→.skip-link:focus {
   768→  position: fixed !important;
   769→  left: var(--spacing-2) !important;
   770→  top: var(--spacing-2) !important;
   771→  width: auto !important;
   772→  height: auto !important;
   773→  overflow: visible !important;
   774→}
   775→
   776→/* Real-time connection indicators */
   777→.connection-indicator {
   778→  display: inline-flex;
   779→  align-items: center;
   780→  gap: var(--spacing-2);
   781→  padding: var(--spacing-1) var(--spacing-2);
   782→  border-radius: var(--radius-full);
   783→  font-size: var(--font-size-xs);
   784→  font-weight: var(--font-weight-medium);
   785→}
   786→
   787→.connection-indicator--connected {
   788→  background: var(--color-success-100);
   789→  color: var(--color-success-700);
   790→  border: 1px solid var(--color-success-200);
   791→}
   792→
   793→.connection-indicator--connecting {
   794→  background: var(--color-warning-100);
   795→  color: var(--color-warning-700);
   796→  border: 1px solid var(--color-warning-200);
   797→}
   798→
   799→.connection-indicator--disconnected {
   800→  background: var(--color-error-100);
   801→  color: var(--color-error-700);
   802→  border: 1px solid var(--color-error-200);
   803→}
   804→
   805→[data-theme="dark"] .connection-indicator--connected {
   806→  background: var(--color-success-950);
   807→  color: var(--color-success-300);
   808→  border: 1px solid var(--color-success-800);
   809→}
   810→
   811→[data-theme="dark"] .connection-indicator--connecting {
   812→  background: var(--color-warning-950);
   813→  color: var(--color-warning-300);
   814→  border: 1px solid var(--color-warning-800);
   815→}
   816→
   817→[data-theme="dark"] .connection-indicator--disconnected {
   818→  background: var(--color-error-950);
   819→  color: var(--color-error-300);
   820→  border: 1px solid var(--color-error-800);
   821→}
   822→
   823→/* Notification badges */
   824→.notification-badge {
   825→  display: inline-flex;
   826→  align-items: center;
   827→  justify-content: center;
   828→  min-width: 1.25rem;
   829→  height: 1.25rem;
   830→  padding: 0 0.25rem;
   831→  font-size: 0.75rem;
   832→  font-weight: var(--font-weight-bold);
   833→  line-height: 1;
   834→  border-radius: var(--radius-full);
   835→  white-space: nowrap;
   836→}
   837→
   838→.notification-badge--primary {
   839→  background: var(--color-primary-500);
   840→  color: white;
   841→}
   842→
   843→.notification-badge--error {
   844→  background: var(--color-error-500);
   845→  color: white;
   846→}
   847→
   848→.notification-badge--warning {
   849→  background: var(--color-warning-500);
   850→  color: white;
   851→}
   852→
   853→.notification-badge--success {
   854→  background: var(--color-success-500);
   855→  color: white;
   856→}
   857→
   858→/* Performance optimizations */
   859→.will-change-transform {
   860→  will-change: transform;
   861→}
   862→
   863→.will-change-opacity {
   864→  will-change: opacity;
   865→}
   866→
   867→.gpu-acceleration {
   868→  transform: translateZ(0);
   869→  backface-visibility: hidden;
   870→  perspective: 1000;
   871→}
   872→
   873→/* Battery optimization styles */
   874→@media (max-width: 768px) {
   875→  .battery-optimize {
   876→    animation-duration: 0s !important;
   877→    transition-duration: 0.1s !important;
   878→  }
   879→  
   880→  .battery-optimize .glass-medium,
   881→  .battery-optimize .glass-strong {
   882→    backdrop-filter: none !important;
   883→    -webkit-backdrop-filter: none !important;
   884→    background: var(--color-surface-primary) !important;
   885→  }
   886→}
   887→
   888→/* Print styles */
   889→@media print {
   890→  .no-print {
   891→    display: none !important;
   892→  }
   893→  
   894→  .notifications-container,
   895→  .chat-widget-container,
   896→  .connection-indicator {
   897→    display: none !important;
   898→  }
   899→  
   900→  * {
   901→    background: transparent !important;
   902→    color: black !important;
   903→    box-shadow: none !important;
   904→    text-shadow: none !important;
   905→  }
   906→}
   907→
   908→/* ===== MOBILE OPTIMIZATIONS ===== */
   909→
   910→/* Touch optimization styles */
   911→.touch-optimized {
   912→  -webkit-tap-highlight-color: transparent;
   913→  -webkit-touch-callout: none;
   914→  -webkit-user-select: none;
   915→  user-select: none;
   916→}
   917→
   918→.touch-optimized button,
   919→.touch-optimized [role="button"],
   920→.touch-optimized a,
   921→.touch-optimized input,
   922→.touch-optimized select,
   923→.touch-optimized textarea {
   924→  -webkit-user-select: text;
   925→  user-select: text;
   926→}
   927→
   928→/* Large touch targets for mobile */
   929→.large-touch-targets button,
   930→.large-touch-targets [role="button"],
   931→.large-touch-targets a,
   932→.large-touch-targets .btn-base {
   933→  min-height: 44px;
   934→  min-width: 44px;
   935→  padding: var(--spacing-3) var(--spacing-4);
   936→}
   937→
   938→.large-touch-targets .btn-sm {
   939→  min-height: 40px;
   940→  min-width: 40px;
   941→}
   942→
   943→.large-touch-targets .btn-lg {
   944→  min-height: 48px;
   945→  min-width: 48px;
   946→}
   947→
   948→/* Performance mode optimizations */
   949→.performance-mode * {
   950→  will-change: auto !important;
   951→  animation-duration: 0.1s !important;
   952→  transition-duration: 0.1s !important;
   953→}
   954→
   955→.performance-mode .glass-medium,
   956→.performance-mode .glass-strong {
   957→  backdrop-filter: none !important;
   958→  -webkit-backdrop-filter: none !important;
   959→  background: var(--color-surface-primary) !important;
   960→}
   961→
   962→/* Reduced animations for accessibility and battery */
   963→.reduce-animations * {
   964→  animation-duration: 0.01ms !important;
   965→  animation-iteration-count: 1 !important;
   966→  transition-duration: 0.01ms !important;
   967→}
   968→
   969→.reduce-animations .animate-pulse,
   970→.reduce-animations .animate-bounce,
   971→.reduce-animations .animate-spin {
   972→  animation: none !important;
   973→}
   974→
   975→/* Mobile-specific breakpoints */
   976→@media (max-width: 480px) {
   977→  /* Extra small mobile phones */
   978→  .responsive-mobile-xs {
   979→    font-size: 14px;
   980→    padding: var(--spacing-2);
   981→  }
   982→  
   983→  .btn-base {
   984→    font-size: var(--font-size-sm);
   985→    padding: var(--spacing-2) var(--spacing-3);
   986→  }
   987→  
   988→  .modal-backdrop {
   989→    backdrop-filter: none;
   990→    -webkit-backdrop-filter: none;
   991→  }
   992→}
   993→
   994→@media (max-width: 640px) {
   995→  /* Small mobile devices */
   996→  .responsive-mobile-sm {
   997→    padding: var(--spacing-3);
   998→  }
   999→  
  1000→  .card-base {
  1001→    border-radius: var(--radius-lg);
  1002→    margin: var(--spacing-2);
  1003→  }
  1004→  
  1005→  .glass-medium,
  1006→  .glass-strong {
  1007→    backdrop-filter: blur(8px) !important;
  1008→    -webkit-backdrop-filter: blur(8px) !important;
  1009→  }
  1010→}
  1011→
  1012→/* Landscape mobile orientation */
  1013→@media (max-height: 500px) and (orientation: landscape) {
  1014→  .landscape-optimize {
  1015→    height: 100vh;
  1016→    overflow-y: auto;
  1017→  }
  1018→  
  1019→  .notifications-container {
  1020→    top: var(--spacing-2);
  1021→    right: var(--spacing-2);
  1022→  }
  1023→  
  1024→  .chat-widget-container {
  1025→    bottom: var(--spacing-2);
  1026→    right: var(--spacing-2);
  1027→  }
  1028→}
  1029→
  1030→/* High DPI displays (retina) */
  1031→@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
