  1036→  
  1037→  .retina-optimize img {
  1038→    image-rendering: -webkit-optimize-contrast;
  1039→    image-rendering: crisp-edges;
  1040→  }
  1041→}
  1042→
  1043→/* Hover detection for touch devices */
  1044→@media (hover: none) {
  1045→  .hover-only {
  1046→    display: none !important;
  1047→  }
  1048→  
  1049→  .btn-base:hover {
  1050→    transform: none !important;
  1051→  }
  1052→  
  1053→  .card-base:hover {
  1054→    transform: none !important;
