  1056→  }
  1057→}
  1058→
  1059→/* Pointer device optimization */
  1060→@media (pointer: coarse) {
  1061→  /* Touch devices */
  1062→  .form-control-base {
  1063→    font-size: 16px; /* Prevents zoom on iOS */
  1064→    padding: var(--spacing-3);
  1065→  }
  1066→  
  1067→  .btn-base {
  1068→    padding: var(--spacing-3) var(--spacing-4);
  1069→  }
  1070→  
  1071→  select,
  1072→  input,
  1073→  textarea {
  1074→    font-size: 16px; /* Prevents zoom on iOS */
  1075→  }
  1076→}
  1077→
  1078→/* Safe area insets for devices with notches */
  1079→@supports (padding: max(0px)) {
  1080→  .safe-area-inset-top {
  1081→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1082→  }
  1083→  
  1084→  .safe-area-inset-bottom {
  1085→    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1086→  }
  1087→  
  1088→  .safe-area-inset-left {
  1089→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1090→  }
  1091→  
  1092→  .safe-area-inset-right {
  1093→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1094→  }
  1095→}
  1096→
  1097→/* PWA display modes */
  1098→@media (display-mode: standalone) {
