  1101→  }
  1102→  
  1103→  .pwa-standalone .navbar {
  1104→    padding-top: env(safe-area-inset-top);
  1105→  }
  1106→}
  1107→
  1108→/* Connection-aware styles */
  1109→.slow-connection .glass-medium,
  1110→.slow-connection .glass-strong {
  1111→  backdrop-filter: none !important;
  1112→  -webkit-backdrop-filter: none !important;
  1113→  background: var(--color-surface-primary) !important;
  1114→}
  1115→
  1116→.slow-connection img {
  1117→  filter: blur(2px);
  1118→  transition: filter var(--transition-base);
  1119→}
  1120→
  1121→.slow-connection img.loaded {
  1122→  filter: none;
