  1124→
  1125→/* Gesture feedback */
  1126→.gesture-feedback {
  1127→  position: relative;
  1128→  overflow: hidden;
  1129→}
  1130→
  1131→.gesture-feedback::after {
  1132→  content: '';
  1133→  position: absolute;
  1134→  top: 50%;
  1135→  left: 50%;
  1136→  width: 0;
  1137→  height: 0;
  1138→  border-radius: 50%;
  1139→  background: rgba(0, 0, 0, 0.1);
