  1142→  pointer-events: none;
  1143→}
  1144→
  1145→.gesture-feedback.active::after {
  1146→  width: 100px;
  1147→  height: 100px;
  1148→}
  1149→
  1150→/* Dark mode gesture feedback */
  1151→[data-theme="dark"] .gesture-feedback::after {
  1152→  background: rgba(255, 255, 255, 0.1);
  1153→}
  1154→
  1155→/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1156→
  1157→/* Swipe gesture indicators */
  1158→.swipe-indicator {
  1159→  position: relative;
  1160→}
  1161→
  1162→.swipe-indicator::before {
  1163→  content: '';
  1164→  position: absolute;
  1165→  top: 50%;
  1166→  right: var(--spacing-4);
  1167→  width: 20px;
  1168→  height: 2px;
  1169→  background: var(--color-gray-400);
  1170→  transform: translateY(-50%);
  1171→  opacity: 0.5;
  1172→}
  1173→
  1174→.swipe-indicator::after {
  1175→  content: '';
  1176→  position: absolute;
  1177→  top: 50%;
  1178→  right: var(--spacing-3);
  1179→  width: 0;
  1180→  height: 0;
  1181→  border-left: 6px solid var(--color-gray-400);
  1182→  border-top: 4px solid transparent;
  1183→  border-bottom: 4px solid transparent;
  1184→  transform: translateY(-50%);
  1185→  opacity: 0.5;
  1186→}
  1187→
  1188→/* Loading states optimized for mobile */
  1189→.mobile-loading {
  1190→  display: flex;
  1191→  flex-direction: column;
  1192→  align-items: center;
  1193→  justify-content: center;
  1194→  min-height: 200px;
  1195→  padding: var(--spacing-8);
  1196→}
  1197→
  1198→.mobile-loading .loading-spinner {
  1199→  width: 40px;
  1200→  height: 40px;
  1201→  border: 3px solid var(--color-gray-200);
  1202→  border-top: 3px solid var(--color-primary-500);
  1203→  border-radius: 50%;
  1204→  animation: spin 1s linear infinite;
