  1212→}
  1213→
  1214→/* Battery optimization styles */
  1215→.battery-optimize {
  1216→  animation-play-state: paused !important;
  1217→}
  1218→
  1219→.battery-optimize * {
  1220→  animation-play-state: paused !important;
  1221→  transition-duration: 0.1s !important;
  1222→}
  1223→
  1224→.battery-optimize .glass-medium,
  1225→.battery-optimize .glass-strong {
  1226→  backdrop-filter: none !important;
  1227→  -webkit-backdrop-filter: none !important;
  1228→  background: var(--color-surface-primary) !important;
  1229→}
  1230→
  1231→.battery-optimize .animate-pulse,
  1232→.battery-optimize .animate-bounce,
  1233→.battery-optimize .animate-spin {
