  1235→}
  1236→
  1237→/* Accessibility enhancements for mobile */
  1238→@media (max-width: 768px) {
  1239→  .mobile-a11y button,
  1240→  .mobile-a11y [role="button"],
  1241→  .mobile-a11y input,
  1242→  .mobile-a11y select {
