#!/usr/bin/env node

/**
 * Build Search Database for Claude Docs MVP
 *
 * This script builds a SQLite FTS5 full-text search database from markdown files
 * in the claude-docs knowledge base. It's designed to be fast, disposable, and
 * easily regenerated.
 *
 * Usage: node build-search-db.js [--verbose]
 */

const fs = require('fs');
const path = require('path');
const Database = require('better-sqlite3');

// Configuration
const DOCS_ROOT = path.join(__dirname, '..');
const DB_PATH = path.join(__dirname, 'search.db');
const TAGS_PATH = path.join(__dirname, 'tags.json');
const RECENT_PATH = path.join(__dirname, 'recent.json');

// Parse arguments
const args = process.argv.slice(2);
const verbose = args.includes('--verbose');

// Color output for terminal
const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  green: '\x1b[32m',
  blue: '\x1b[34m',
  yellow: '\x1b[33m',
  red: '\x1b[31m'
};

function log(message, color = '') {
  console.log(`${color}${message}${colors.reset}`);
}

function logStep(step, message) {
  log(`${step} ${message}`, colors.bright);
}

function logSuccess(message) {
  log(`âœ… ${message}`, colors.green);
}

function logError(message) {
  log(`âŒ ${message}`, colors.red);
}

/**
 * Extract metadata from markdown content
 */
function extractMetadata(content, filePath) {
  const metadata = {
    tags: [],
    title: '',
    date: '',
    type: '',
    confidence: ''
  };

  // Extract title (first # heading)
  const titleMatch = content.match(/^#\s+(.+)$/m);
  metadata.title = titleMatch ? titleMatch[1].trim() : path.basename(filePath, '.md');

  // Extract tags from **Tags:** pattern
  const tagsMatch = content.match(/\*\*Tags:\*\*\s*(.+)/i);
  if (tagsMatch) {
    metadata.tags = tagsMatch[1]
      .split(',')
      .map(t => t.trim())
      .filter(t => t.length > 0);
  }

  // Extract date
  const dateMatch = content.match(/\*\*Date.*?:\*\*\s*(\d{4}-\d{2}-\d{2})/);
  metadata.date = dateMatch ? dateMatch[1] : '';

  // Extract type
  const typeMatch = content.match(/\*\*Type:\*\*\s*(pattern|gotcha|decision|discovery)/i);
  metadata.type = typeMatch ? typeMatch[1].toLowerCase() : '';

  // Extract confidence
  const confidenceMatch = content.match(/\*\*Confidence:\*\*\s*(high|medium|low)/i);
  metadata.confidence = confidenceMatch ? confidenceMatch[1].toLowerCase() : '';

  return metadata;
}

/**
 * Recursively find all markdown files in a directory
 */
function findMarkdownFiles(dir, files = []) {
  const entries = fs.readdirSync(dir, { withFileTypes: true });

  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);

    if (entry.isDirectory()) {
      // Skip .index directory
      if (entry.name !== '.index') {
        findMarkdownFiles(fullPath, files);
      }
    } else if (entry.isFile() && entry.name.endsWith('.md')) {
      files.push(fullPath);
    }
  }

  return files;
}

/**
 * Build tag mappings from files
 */
function buildTagMappings(filesData) {
  const tagMap = {};

  for (const fileData of filesData) {
    for (const tag of fileData.metadata.tags) {
      if (!tagMap[tag]) {
        tagMap[tag] = [];
      }

      // Store relative path
      const relativePath = path.relative(process.cwd(), fileData.path);

      if (!tagMap[tag].includes(relativePath)) {
        tagMap[tag].push(relativePath);
      }
    }
  }

  return tagMap;
}

/**
 * Main build function
 */
async function buildDatabase() {
  const startTime = Date.now();

  logStep('ðŸ”', 'Scanning for markdown files...');

  // Find all markdown files in knowledge/ and architecture/
  const knowledgeFiles = findMarkdownFiles(path.join(DOCS_ROOT, 'knowledge'));
  const architectureFiles = findMarkdownFiles(path.join(DOCS_ROOT, 'architecture'));
  const allFiles = [...knowledgeFiles, ...architectureFiles];

  log(`   Found ${allFiles.length} markdown files`);

  if (allFiles.length === 0) {
    logError('No markdown files found!');
    process.exit(1);
  }

  logStep('ðŸ“–', 'Reading file contents...');

  // Read all files and extract metadata
  const filesData = [];
  let processed = 0;

  for (const filePath of allFiles) {
    try {
      const content = fs.readFileSync(filePath, 'utf-8');
      const metadata = extractMetadata(content, filePath);
      const relativePath = path.relative(process.cwd(), filePath);

      filesData.push({
        path: relativePath,
        content,
        metadata
      });

      processed++;

      if (verbose && processed % 10 === 0) {
        log(`   Processed ${processed}/${allFiles.length} files...`);
      }
    } catch (err) {
      logError(`Failed to read ${filePath}: ${err.message}`);
    }
  }

  logSuccess(`Read ${filesData.length} files`);

  logStep('ðŸ’¾', 'Creating SQLite database...');

  // Delete existing database
  if (fs.existsSync(DB_PATH)) {
    fs.unlinkSync(DB_PATH);
  }

  // Create new database
  const db = new Database(DB_PATH);

  try {
    // Create FTS5 table
    db.exec(`
      CREATE VIRTUAL TABLE IF NOT EXISTS fts_docs USING fts5(
        file_path UNINDEXED,
        content,
        tags UNINDEXED,
        title UNINDEXED,
        date UNINDEXED,
        type UNINDEXED,
        confidence UNINDEXED,
        tokenize = 'porter'
      )
    `);

    logSuccess('Created FTS5 table');

    logStep('ðŸ“', 'Inserting documents...');

    // Prepare insert statement
    const insert = db.prepare(`
      INSERT INTO fts_docs (file_path, content, tags, title, date, type, confidence)
      VALUES (?, ?, ?, ?, ?, ?, ?)
    `);

    // Insert all documents in a transaction (faster)
    const insertMany = db.transaction((docs) => {
      for (const doc of docs) {
        insert.run(
          doc.path,
          doc.content,
          doc.metadata.tags.join(', '),
          doc.metadata.title,
          doc.metadata.date,
          doc.metadata.type,
          doc.metadata.confidence
        );
      }
    });

    insertMany(filesData);

    logSuccess(`Inserted ${filesData.length} documents`);

    // Get database statistics
    const stats = db.prepare('SELECT COUNT(*) as count FROM fts_docs').get();
    const dbSize = fs.statSync(DB_PATH).size;

    log(`   Database size: ${(dbSize / 1024).toFixed(2)} KB`);
    log(`   Documents indexed: ${stats.count}`);

  } catch (err) {
    logError(`Database error: ${err.message}`);
    throw err;
  } finally {
    db.close();
  }

  logStep('ðŸ·ï¸', 'Building tag mappings...');

  // Build tag mappings
  const tagMappings = buildTagMappings(filesData);

  // Write tags.json
  fs.writeFileSync(TAGS_PATH, JSON.stringify(tagMappings, null, 2));

  logSuccess(`Mapped ${Object.keys(tagMappings).length} unique tags`);

  if (verbose) {
    log('\nTop 10 tags:');
    const sortedTags = Object.entries(tagMappings)
      .sort((a, b) => b[1].length - a[1].length)
      .slice(0, 10);

    for (const [tag, files] of sortedTags) {
      log(`   ${tag}: ${files.length} files`);
    }
  }

  logStep('â°', 'Updating recent access log...');

  // Load or create recent.json
  let recent = {};
  if (fs.existsSync(RECENT_PATH)) {
    recent = JSON.parse(fs.readFileSync(RECENT_PATH, 'utf-8'));
  }

  // Update last reindex time
  recent.__lastReindex = new Date().toISOString();

  // Write recent.json
  fs.writeFileSync(RECENT_PATH, JSON.stringify(recent, null, 2));

  logSuccess('Updated access log');

  // Calculate duration
  const duration = Date.now() - startTime;

  log('');
  logSuccess(`Reindex complete! (${duration}ms)`);
  log('');
  log(`ðŸ“„ ${DB_PATH}`, colors.blue);
  log(`ðŸ·ï¸  ${TAGS_PATH}`, colors.blue);
  log(`â° ${RECENT_PATH}`, colors.blue);
  log('');
  log('Test search:', colors.yellow);
  log(`  sqlite3 ${DB_PATH} "SELECT file_path FROM fts_docs WHERE fts_docs MATCH 'pattern' LIMIT 5"`, colors.yellow);
  log('');
}

// Run the build
buildDatabase().catch(err => {
  logError(`Build failed: ${err.message}`);
  if (verbose) {
    console.error(err.stack);
  }
  process.exit(1);
});

