   115→
   116→.label-position {
   117→  top: min(min(-1.5vh, -1.5vw), -1.5rem);
   118→}
   119→
   120→/* Prose styles for documentation */
   121→.prose {
   122→  @apply text-gray-900 dark:text-gray-100;
   123→}
   124→
   125→.prose h1,
   126→.prose h2,
   127→.prose h3,
   128→.prose h4,
   129→.prose h5,
   130→.prose h6 {
   131→  @apply text-gray-900 dark:text-white font-semibold;
   132→}
   133→
   134→.prose h1 {
   135→  @apply text-4xl sm:text-5xl leading-tight mt-12 mb-6;
   136→}
   137→
   138→.prose h2 {
   139→  @apply text-3xl sm:text-4xl leading-snug mt-10 mb-5;
   140→}
   141→
   142→.prose h3 {
   143→  @apply text-2xl sm:text-3xl leading-snug mt-8 mb-4;
   144→}
   145→
   146→.prose h4 {
   147→  @apply text-xl sm:text-2xl leading-snug mt-6 mb-3;
   148→}
   149→
   150→.prose h5 {
   151→  @apply text-lg leading-snug mt-5 mb-2;
   152→}
   153→
   154→.prose h6 {
   155→  @apply text-base uppercase tracking-wide text-gray-600 dark:text-gray-400 mt-4 mb-2;
   156→}
   157→
   158→.prose a {
   159→  @apply text-primary-600 dark:text-primary-400 hover:text-primary-700 dark:hover:text-primary-300 underline decoration-primary-300 underline-offset-2 transition-colors;
   160→}
   161→
   162→
   163→.prose p {
   164→  @apply text-gray-800 dark:text-gray-200 leading-relaxed mt-4 mb-4;
   165→}
   166→
   167→.prose code {
   168→  @apply bg-gray-100 dark:bg-gray-800 text-primary-700 dark:text-primary-300 px-1.5 py-0.5 rounded font-mono text-sm;
   169→}
   170→
   171→.prose pre {
   172→  @apply bg-gray-100 dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg p-4 my-6 overflow-x-auto;
   173→}
   174→
   175→.prose pre code {
   176→  @apply bg-transparent text-gray-900 dark:text-gray-100 px-0 py-0;
   177→}
   178→
   179→.prose strong {
   180→  @apply text-gray-900 dark:text-white font-semibold;
   181→}
   182→
   183→.prose em {
   184→  @apply text-gray-700 dark:text-gray-200;
   185→}
   186→
   187→
   188→.prose blockquote {
   189→  @apply border-l-4 border-l-primary-500 dark:border-l-primary-400 text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-gray-800/50 px-5 py-4 rounded-r italic my-6;
   190→}
   191→
   192→.prose ul,
   193→.prose ol {
   194→  @apply my-5 pl-6;
   195→}
   196→
   197→.prose ul {
   198→  @apply list-disc;
   199→}
   200→
   201→.prose ol {
   202→  @apply list-decimal;
   203→}
   204→
   205→.prose li {
   206→  @apply my-2;
   207→}
   208→
   209→.prose ul li,
   210→.prose ol li {
   211→  @apply text-gray-800 dark:text-gray-200;
   212→}
   213→
   214→.prose ul li::marker,
   215→.prose ol li::marker {
   216→  @apply text-primary-600 dark:text-primary-400;
   217→}
   218→
   219→.prose hr {
   220→  @apply border-gray-300 dark:border-gray-700 my-10;
   221→}
   222→
   223→/* Table styles for documentation */
   224→.prose table {
   225→  @apply w-full border-collapse my-6;
   226→}
   227→
   228→.prose thead {
   229→  @apply border-b-2 border-gray-300 dark:border-gray-700;
   230→}
   231→
   232→.prose th {
   233→  @apply px-4 py-2 text-left font-semibold text-gray-900 dark:text-white;
   234→}
   235→
   236→.prose td {
   237→  @apply px-4 py-2 border-t border-gray-200 dark:border-gray-700;
   238→}
   239→
   240→.prose tbody tr:hover {
   241→  @apply bg-gray-50 dark:bg-gray-800;
   242→}
   243→
   244→.prose img,
   245→.prose video,
   246→.prose picture {
   247→  @apply my-6 rounded-lg shadow-sm;
   248→}
   249→
   250→/* ===== GRADIENT UTILITY CLASSES ===== */
   251→
   252→/* Hero/Landing Page Gradients - Layered radial approach */
   253→.bg-gradient-hero {
   254→  background:
   255→    var(--gradient-hero-radial-1),
   256→    var(--gradient-hero-radial-2),
   257→    var(--gradient-hero-radial-3),
   258→    var(--gradient-hero-radial-4),
   259→    var(--gradient-hero-radial-5),
   260→    var(--gradient-hero-base);
   261→}
   262→
   263→.bg-gradient-hero-vibrant {
   264→  background: var(--gradient-hero-vibrant);
   265→}
   266→
   267→.bg-gradient-hero-subtle {
   268→  background: var(--gradient-hero-subtle);
   269→}
   270→
   271→/* Accent Gradients */
   272→.bg-gradient-accent-primary {
   273→  background: var(--gradient-accent-primary);
   274→}
   275→
   276→.bg-gradient-accent-purple {
   277→  background: var(--gradient-accent-purple);
   278→}
   279→
   280→.bg-gradient-accent-warm {
   281→  background: var(--gradient-accent-warm);
   282→}
   283→
   284→/* Feature Section Gradients */
   285→.bg-gradient-feature-light {
   286→  background: var(--gradient-feature-light);
   287→}
   288→
   289→.bg-gradient-feature-card {
   290→  background: var(--gradient-feature-card);
   291→}
   292→
   293→/* CTA/Interactive Gradients with Glow Effects */
   294→.bg-gradient-cta {
   295→  background: var(--gradient-cta);
   296→  box-shadow: 0 0 20px rgba(0, 124, 137, 0.3);
   297→  transition: all 0.3s ease;
   298→}
   299→
   300→.bg-gradient-cta:hover {
   301→  background: var(--gradient-cta-hover);
   302→  box-shadow: 0 0 40px rgba(0, 124, 137, 0.5);
   303→  transform: translateY(-2px);
   304→}
   305→
   306→.bg-gradient-cta:active {
   307→  transform: translateY(0);
   308→  box-shadow: 0 0 30px rgba(0, 124, 137, 0.4);
   309→}
   310→
   311→/* Mesh/Complex Gradients */
   312→.bg-gradient-mesh {
   313→  background: var(--gradient-mesh);
   314→}
   315→
   316→/* Optional Mesh Overlay - Add subtle depth to any element */
   317→.bg-gradient-mesh-overlay {
   318→  position: relative;
   319→}
   320→
   321→.bg-gradient-mesh-overlay::before {
   322→  content: '';
   323→  position: absolute;
   324→  inset: 0;
   325→  background: var(--gradient-mesh-overlay);
   326→  pointer-events: none;
   327→  z-index: 1;
   328→}
   329→
   330→/* Text Gradients */
   331→.text-gradient-primary {
   332→  background: var(--gradient-accent-primary);
   333→  -webkit-background-clip: text;
   334→  background-clip: text;
   335→  -webkit-text-fill-color: transparent;
   336→}
   337→
   338→.text-gradient-purple {
   339→  background: var(--gradient-accent-purple);
   340→  -webkit-background-clip: text;
   341→  background-clip: text;
   342→  -webkit-text-fill-color: transparent;
   343→}
   344→
   345→.text-gradient-warm {
   346→  background: var(--gradient-accent-warm);
   347→  -webkit-background-clip: text;
   348→  background-clip: text;
   349→  -webkit-text-fill-color: transparent;
   350→}
   351→
   352→/* Animated Gradients */
   353→@keyframes gradientShift {
   354→  0%, 100% {
   355→    background-position: 0% 50%;
   356→  }
   357→  50% {
   358→    background-position: 100% 50%;
   359→  }
   360→}
   361→
   362→.bg-gradient-animated {
   363→  background-size: 200% 200%;
   364→  animation: gradientShift 8s ease infinite;
   365→}
   366→
   367→/* Responsive Gradient Utilities */
   368→@media (max-width: 768px) {
   369→  .bg-gradient-hero,
   370→  .bg-gradient-hero-vibrant {
   371→    /* Simpler gradients on mobile for performance */
   372→    background: var(--gradient-hero-subtle);
   373→  }
   374→}
   375→

