    10→import SessionInitializer from "@vue/global/SessionInitializer.vue";
    11→import MetadataTags from "@components/global/MetadataTags.astro";
    12→import ClarityInit from "@components/global/ClarityInit.astro";
    13→import CookieGuard from "@vue/guards/CookieGuard.vue";
    14→import DocsModal from "@vue/modals/DocsModal.vue";
    15→import { SITE_DATA } from "@/constants/siteData";
    16→import { getCollection } from 'astro:content';
    17→import { docCategories } from '@/content/docs/categories';
    18→import type { DocSummary } from '@/types/docs';
    19→
    20→const siteData = SITE_DATA;
    21→
    22→interface Props {
    23→  title: string;
    24→}
    25→
    26→const sessionUser = await Astro.session?.get("authUser");
    27→
    28→const { title } = Astro.props;
    29→
    30→// Fetch doc summaries for DocsModal without shipping full markdown bodies
    31→const docsCollection = await getCollection('docs');
    32→const docSummaries: DocSummary[] = docsCollection.map((doc) => ({
    33→  slug: doc.slug,
    34→  title: doc.data.title,
    35→  description: doc.data.description,
    36→  category: doc.data.category,
    37→  order: doc.data.order ?? null,
    38→  tags: doc.data.tags,
    39→  relatedSections: doc.data.relatedSections,
    40→  lastUpdated: doc.data.lastUpdated ? doc.data.lastUpdated.toISOString() : null,
    41→  author: doc.data.author ?? null,
    42→}));
    43→
    44→// Prevent caching of authenticated pages to avoid logout issues
    45→Astro.response.headers.set('Cache-Control', 'private, no-cache, no-store, must-revalidate');
    46→Astro.response.headers.set('Pragma', 'no-cache');
    47→Astro.response.headers.set('Expires', '0');
    48→---
    49→
    50→<!doctype html>
    51→<html lang="en">
    52→  <head>
    53→    <MetadataTags title={title} description={siteData.description} />
    54→    <style is:global>
    55→      html {
    56→        --bg-color-default: white;
    57→        --text-color-default: black;
    58→        color: black;
    59→        background-color: white;
    60→        font-family: system-ui, sans-serif;
    61→      }
    62→
    63→      html,
    64→      body,
    65→      #body {
    66→        height: 100% !important;
    67→      }
    68→
    69→      [data-theme="dark"] {
    70→        --bg-color-default: #333;
    71→        --text-color-default: white;
    72→        color: white;
    73→        background-color: #333;
    74→      }
    75→
    76→      code {
    77→        font-family:
    78→          Menlo,
    79→          Monaco,
    80→          Lucida Console,
    81→          Liberation Mono,
    82→          DejaVu Sans Mono,
    83→          Bitstream Vera Sans Mono,
    84→          Courier New,
    85→          monospace;
    86→      }
    87→    </style>
    88→    <ClarityInit />
    89→  </head>
    90→  <body
    91→    id="body"
    92→    class="relative min-h-screen flex flex-col overflow-hidden touch-optimized mobile-a11y"
    93→  >
    94→    <Navbar client:load transition:persist user={sessionUser} />
    95→    <div class="flex-1 flex overflow-hidden">
    96→      <SideMenu client:load />
    97→      <div
    98→        class="flex-1 flex flex-col overflow-y-auto bg-gray-100 dark:bg-gray-900 transition-colors duration-300"
    99→      >
   100→        <DataProvider client:load>
   101→          <slot />
   102→        </DataProvider>
   103→        <Footer client:load transition:persist />
   104→      </div>
   105→    </div>
   106→
   107→    <CookieGuard client:load transition:persist />
   108→    <CreatePostProvider client:load />
   109→    <SessionInitializer client:load />
   110→
   111→    <!-- Teleport Layer: keep this as a single top-level target for modals/popovers -->
   112→    <div id="teleport-layer"></div>
   113→
   114→    <!-- Global Action Widget -->
   115→    <GlobalActionWidgetWithOverrides
   116→      client:load
   117→      position="bottom-right"
   118→      glass="subtle"
   119→      show-chat-actions={true}
   120→      transition:persist="global-action-widget"
   121→    />
   122→    
   123→    <!-- Smart Notifications -->
   124→    <SmartNotifications
   125→      client:load
   126→      position="top-right"
   127→      max-visible={5}
   128→      enable-ai-prioritization={true}
   129→      enable-stacking-prevention={true}
   130→      enable-smart-grouping={true}
   131→      transition:persist
   132→    />
   133→
   134→    <!-- Documentation Modal -->
   135→    <DocsModal
   136→      client:idle
   137→      docs={docSummaries}
   138→      categories={docCategories}
   139→    />
   140→
   141→  </body>
   142→</html>
