// Track which steps have been visible at least once
const hasBeenVisible = ref<boolean[]>([false, false, false]);

// Watch for visibility changes and mark steps as "seen" permanently
watch(step1Visible, (visible) => {
  if (visible) hasBeenVisible.value[0] = true;
});

watch(step2Visible, (visible) => {
  if (visible) hasBeenVisible.value[1] = true;
});

watch(step3Visible, (visible) => {
  if (visible) hasBeenVisible.value[2] = true;
});

// GIF handling (reuse same asset as hero)
const heroGifPath = '/herogif.gif';
const gifRef = ref<HTMLImageElement | null>(null);
const gifLoaded = ref(false);

const onGifLoad = () => {
  gifLoaded.value = true;
};

const loadGifWithDelay = () => {
  window.setTimeout(() => {
    if (gifRef.value) gifRef.value.src = heroGifPath;
  }, 300);
};

onMounted(() => {
  nextTick(() => {
    if ('requestIdleCallback' in window) {
      (window as any).requestIdleCallback(() => loadGifWithDelay());
    } else {
      loadGifWithDelay();
    }
  });
});

