   204→export const getNextSubstep = (currentStep: OnboardingStep, currentSubstep: string) => {
   205→  const stepIndex = ONBOARDING_STEPS.findIndex(s => s.id === currentStep);
   206→  const stepConfig = ONBOARDING_STEPS[stepIndex];
   207→  const substepIndex = (stepConfig.substeps as readonly string[]).indexOf(currentSubstep);
   208→
   209→  // Next substep in current step
   210→  if (substepIndex < stepConfig.substeps.length - 1) {
   211→    return { step: currentStep, substep: stepConfig.substeps[substepIndex + 1] };
   212→  }
   213→
   214→  // First substep of next step
   215→  const nextStep = ONBOARDING_STEPS[stepIndex + 1];
   216→  if (nextStep) {
   217→    return { step: nextStep.id as OnboardingStep, substep: nextStep.substeps[0] };
   218→  }
   219→
   220→  // No next step
   221→  return null;
   222→};
