   225→  const stepIndex = ONBOARDING_STEPS.findIndex(s => s.id === currentStep);
   226→  const stepConfig = ONBOARDING_STEPS[stepIndex];
   227→  const substepIndex = (stepConfig.substeps as readonly string[]).indexOf(currentSubstep);
   228→
   229→  // Previous substep in current step
   230→  if (substepIndex > 0) {
   231→    return { step: currentStep, substep: stepConfig.substeps[substepIndex - 1] };
   232→  }
   233→
   234→  // Last substep of previous step
   235→  const prevStep = ONBOARDING_STEPS[stepIndex - 1];
   236→  if (prevStep) {
   237→    const lastSubstep = prevStep.substeps[prevStep.substeps.length - 1];
   238→    return { step: prevStep.id as OnboardingStep, substep: lastSubstep };
   239→  }
   240→
   241→  // No previous step
   242→  return null;
   243→};
