   255→export const getTargetIndexFromSubstep = (substep: string): number => {
   256→  if (isDynamicTargetStep(substep)) {
   257→    const match = substep.match(/connect_target_account_(\d+)/);
   258→    return match ? parseInt(match[1], 10) : 0;
   259→  }
   260→  return -1;
   261→};
   262→
   263→export const generateDynamicSubsteps = (stepId: string, data: OnboardingData): string[] => {
   264→  const staticStep = ONBOARDING_STEPS.find(s => s.id === stepId);
   265→  if (!staticStep) return [];
   266→
   267→  if (stepId === 'accounts_workflows') {
   268→    const substeps = [...staticStep.substeps] as string[];
   269→    const platforms = getTargetPlatforms(data);
   270→
   271→    if (platforms.length > 0) {
   272→      // Find where to insert dynamic steps (after connect_trigger_account)
   273→      const triggerIndex = substeps.indexOf('connect_trigger_account');
   274→
   275→      if (triggerIndex !== -1) {
   276→        // Insert dynamic target connection steps
   277→        for (let i = 0; i < platforms.length; i++) {
   278→          substeps.splice(triggerIndex + 1 + i, 0, `connect_target_account_${i}` as any);
   279→        }
   280→      }
   281→    }
   282→
   283→    // Find where to insert generate_workflow step (before ai_workflow_enhancements)
   284→    const enhancementsIndex = substeps.indexOf('ai_workflow_enhancements');
   285→    if (enhancementsIndex !== -1) {
   286→      substeps.splice(enhancementsIndex, 0, 'generate_workflow' as any);
   287→    }
   288→
   289→    return substeps;
   290→  }
   291→
   292→  return [...staticStep.substeps] as string[];
   293→};
   294→
   295→// Validation helper with dynamic step support
   296→export const isSubstepComplete = (substep: string, data: OnboardingData): boolean => {
   297→
   298→  // Handle dynamic target connection steps
   299→  if (isDynamicTargetStep(substep)) {
   300→    const index = getTargetIndexFromSubstep(substep);
   301→    const accounts = data.connectedTargetAccounts || [];
   302→    const account = accounts[index];
   303→
   304→    if (!account) return false;
   305→
   306→    // Handle pending_selection status for related accounts
   307→    if (account.status === 'pending_selection') {
   308→      // Require related account selection to proceed
   309→      return (account.relatedAccounts?.length || 0) > 0 &&
   310→             account.selectedRelatedAccountId !== undefined;
   311→    }
   312→
   313→    // First target is required
   314→    if (index === 0) {
   315→      return account.status === 'connected';
   316→    }
   317→
   318→    // Additional targets are optional (can be skipped)
   319→    return account.status === 'connected' || account.status === 'skipped';
   320→  }
   321→
   322→  // Handle trigger account connection with new schema
   323→  if (substep === 'connect_trigger_account') {
   324→    const trigger = data.triggerConnection;
   325→    if (!trigger) return false;
   326→
   327→    // Handle pending_selection status for related accounts
   328→    if (trigger.status === 'pending_selection') {
   329→      // Check if we have the new relatedAccounts field
   330→      const relatedAccounts = (trigger as any).relatedAccounts;
   331→      const selectedRelatedAccountId = (trigger as any).selectedRelatedAccountId;
   332→      return (relatedAccounts?.length || 0) > 0 && selectedRelatedAccountId !== undefined;
   333→    }
   334→
   335→    const result = trigger.status === 'connected';
   336→    if (!result) {
   337→      console.log('[OAuth Debug] Trigger account not connected yet, status:', trigger.status);
   338→    }
   339→    return result;
   340→  }
   341→
   342→  // Handle generate_workflow substep
   343→  if (substep === 'generate_workflow') {
   344→    return data.workflowCreated === true && data.workflowId !== undefined;
   345→  }
   346→
   347→  // Handle static fields
   348→  const fields = SUBSTEP_FIELDS[substep as keyof typeof SUBSTEP_FIELDS];
   349→  if (!fields) return true;
   350→
   351→  const fieldList = Array.isArray(fields) ? fields : [fields];
   352→  return fieldList.every(field => {
   353→    const value = data[field as keyof OnboardingData];
   354→    if (Array.isArray(value)) return value.length > 0;
   355→    if (typeof value === 'boolean') return value === true;
   356→    if (typeof value === 'string' && value === 'connected') return true;
   357→
   358→    // Handle new triggerConnection object
   359→    if (field === 'triggerConnection' && typeof value === 'object') {
   360→      const result = (value as any).status === 'connected';
   361→      console.log('[OAuth Debug] generic triggerConnection path:', {
   362→        field,
   363→        value,
   364→        status: (value as any).status,
   365→        result
   366→      });
   367→      return result;
   368→    }
   369→
   370→    return value !== undefined && value !== null && value !== 'not started';
   371→  });
   372→};
   373→
   374→// Check if all required targets are connected
   375→export const areRequiredTargetsConnected = (data: OnboardingData): boolean => {
   376→  const accounts = data.connectedTargetAccounts || [];
   377→
   378→  // First target must be connected
   379→  if (accounts.length === 0) return false;
   380→  if (accounts[0]?.status !== 'connected') return false;
   381→
   382→  // Other targets can be skipped
   383→  return accounts.slice(1).every(
   384→    account => account.status === 'connected' || account.status === 'skipped'
   385→  );
   386→};
   387→
