   389→export const formatPlatform = (platform: Platform): string => {
   390→  return PLATFORM_CONFIG[platform]?.name || platform;
   391→};
   392→
   393→// Helper function to get platform icon
   394→export const getPlatformIcon = (platform: Platform): string => {
   395→  return PLATFORM_CONFIG[platform]?.icon || 'mdi:help-circle';
   396→};
