import { z } from "zod";

// Platform and status enums
export const PLATFORM_OPTIONS = [
  "instagram", "threads", "facebook", "facebook_pages", "tiktok", "linkedIn",
  "youtube", "pinterest", "mastodon", "bluesky", "google"
] as const;

export type Platform = typeof PLATFORM_OPTIONS[number];

export const ACCOUNT_STATUS_OPTIONS = [
  "not started", "connecting", "selection_required", "pending_selection", "connected", "failed",
  "expired", "reconnect_required", "skipped", "quit here"
] as const;

export type AccountStatus = typeof ACCOUNT_STATUS_OPTIONS[number];

// Platform metadata for UI components
export const PLATFORM_CONFIG = {
  instagram: { name: 'Instagram', icon: 'mdi:instagram' },
  facebook: { name: 'Facebook', icon: 'mdi:facebook' },
  facebook_pages: { name: 'Facebook (for Facebook Pages)', icon: 'mdi:facebook' },
  tiktok: { name: 'TikTok', icon: 'simple-icons:tiktok' },
  linkedIn: { name: 'LinkedIn', icon: 'mdi:linkedin' },
  youtube: { name: 'Google (for YouTube)', icon: 'mdi:youtube' },
  pinterest: { name: 'Pinterest', icon: 'mdi:pinterest' },
  threads: { name: 'Threads', icon: 'simple-icons:threads' },
  mastodon: { name: 'Mastodon', icon: 'simple-icons:mastodon' },
  bluesky: { name: 'Bluesky', icon: 'simple-icons:bluesky' },
  google: { name: 'Google', icon: 'mdi:google' }
} as const;

// Existing account interface for reuse functionality
export interface ExistingAccount {
  oauthAccountId: string;
  accountName: string;
  accountHandle?: string;
  avatar?: string;
  platform: Platform;
  isActive: boolean;
  connectedAt: string;
}

// Target account schema with OAuth metadata
export const TargetAccountSchema = z.object({
  platform: z.enum(PLATFORM_OPTIONS),
  status: z.enum(ACCOUNT_STATUS_OPTIONS).default("not started"),
  // Core fields
  accountId: z.string().optional(),
  accountName: z.string().optional(),
  accountHandle: z.string().optional(),
  // OAuth linkage metadata
  oauthAccountId: z.string().optional(), // Reference to OAuth account
  errorMessage: z.string().optional(),   // Error tracking
  connectedAt: z.string().optional(),    // Timestamp
  // Multi-account metadata
  parentOAuthAccountId: z.string().optional(),     // Facebook/Google parent connection
  relatedAccounts: z.array(z.object({              // Cached related accounts for selection UI
    oauthAccountId: z.string(),
    accountId: z.string(),
    name: z.string().optional(),
    handle: z.string().optional(),
    platform: z.string(),
    avatar: z.string().optional()
  })).optional(),
  selectedRelatedAccountId: z.string().optional(), // Final chosen channel/page id
});

// Single unified schema for all onboarding data
export const OnboardingSchema = z.object({
  // Personalization data
  describesYou: z.enum(["Individual Creator", "Small Business", "Agency/Freelancer", "Enterprise"]).optional(),
  challenge: z.array(z.string()).optional(),
  goals: z.array(z.string()).optional(),
  weeklyFrequency: z.enum(["1-2", "2-4", "5-7", "10-14", "custom"]).optional(),
  customFrequency: z.number().optional(),
  contentTypes: z.array(z.enum(["photos", "videos", "stories", "articles", "infographics", "text only", "polls & surveys", "live streams"])).optional(),
  platformsUsed: z.array(z.enum(PLATFORM_OPTIONS)).optional(),

  // Workflow data
  platformTrigger: z.enum(PLATFORM_OPTIONS).optional(),
  platformTargets: z.array(z.enum(PLATFORM_OPTIONS)).default([]),

  // Target account tracking
  connectedTargetAccounts: z.array(TargetAccountSchema).default([]),
  currentTargetIndex: z.number().default(0),
  requiresRelatedSelection: z.boolean().default(false),

  // OAuth trigger connection data
  triggerConnection: z.object({
    status: z.enum(ACCOUNT_STATUS_OPTIONS).default("not started"),
    oauthAccountId: z.string().optional(),
    accountName: z.string().optional(),
    accountHandle: z.string().optional(),
    errorMessage: z.string().optional(),
    connectedAt: z.string().optional(),
    // Multi-account metadata (for Google/Facebook flows)
    parentOAuthAccountId: z.string().optional(),     // Facebook/Google parent connection
    relatedAccounts: z.array(z.object({              // Cached related accounts for selection UI
      oauthAccountId: z.string(),
      accountId: z.string(),
      name: z.string().optional(),
      handle: z.string().optional(),
      platform: z.string(),
      avatar: z.string().optional()
    })).optional(),
    selectedRelatedAccountId: z.string().optional(), // Final chosen channel/page id
  }).default({ status: "not started" }),

  workflowId: z.string().optional(),
  workflowCreated: z.boolean().default(false),
  activated: z.enum(["not started", "quit here", "errored", "skipped", "true"]).default("not started"),
  tested: z.enum(["not started", "quit here", "errored", "skipped", "true"]).default("not started"),
  upgraded: z.enum(["not started", "quit here", "errored", "skipped", "true"]).default("not started"),

  // Progress tracking
  completed: z.boolean().default(false),
  currentStep: z.enum(["welcome", "personalization", "accounts_workflows", "completion"]).default("welcome"),
  currentSubstep: z.string().optional(),

  // Metadata
  sessionId: z.string().optional(),
  userId: z.string().optional(),
  teamId: z.string().optional(),
  startedAt: z.string().optional(),
  completedAt: z.string().optional(),

  // Database fields
  $id: z.string().optional(),
  $createdAt: z.string().optional(),
  $updatedAt: z.string().optional(),
  $permissions: z.array(z.string()).optional(),
});

export type OnboardingData = z.infer<typeof OnboardingSchema>;

// Step types
export type OnboardingStep = "welcome" | "personalization" | "accounts_workflows" | "completion";

// Simple step configuration
export const ONBOARDING_STEPS = [
  {
    id: "welcome",
    title: "Welcome",
    substeps: ["welcome_start"]
  },
  {
    id: "personalization",
    title: "Personalization",
    substeps: [
      // "personalization_start",
      "describes_you",
      // "biggest_challenge",
      // "main_goals",
      // "posting_frequency",
      // "content_type",
      "platform_use"
    ]
  },
  {
    id: "accounts_workflows",
    title: "Accounts and Workflows",
    substeps: [
      "accounts_workflow_start",
      "post_from_here_to_here",
      "connect_trigger_account",
      // "customize_conditions_options_schedule",
      "ai_workflow_enhancements",
      "platform_customizations",
      "activate_workflow_and_test"
    ]
  },
  {
    id: "completion",
    title: "Completion",
    substeps: [
      // "completion_start",
      // "questions_or_need_further_guidance",
      // "billing_tiers",
      "celebration_and_more"
    ]
  }
] as const;

// Map substeps to their data fields (with dynamic step support)
export const SUBSTEP_FIELDS = {
  describes_you: 'describesYou',
  // biggest_challenge: 'challenge',
  //main_goals: 'goals',
  //posting_frequency: 'weeklyFrequency',
  //content_type: 'contentTypes',
  platform_use: 'platformsUsed',
  post_from_here_to_here: ['platformTrigger', 'platformTargets'],
  connect_trigger_account: 'triggerConnection',
  generate_workflow: ['workflowId', 'workflowCreated'],
  ai_workflow_enhancements: 'workflowCreated',
  platform_customizations: 'workflowCreated',
  activate_workflow_and_test: ['activated', 'tested'],
  //billing_tiers: 'upgraded',
  celebration_and_more: 'completed'
} as const;

// Navigation helpers
export const getNextSubstep = (currentStep: OnboardingStep, currentSubstep: string) => {
  const stepIndex = ONBOARDING_STEPS.findIndex(s => s.id === currentStep);
  const stepConfig = ONBOARDING_STEPS[stepIndex];
  const substepIndex = (stepConfig.substeps as readonly string[]).indexOf(currentSubstep);

  // Next substep in current step
  if (substepIndex < stepConfig.substeps.length - 1) {
    return { step: currentStep, substep: stepConfig.substeps[substepIndex + 1] };
  }

  // First substep of next step
  const nextStep = ONBOARDING_STEPS[stepIndex + 1];
  if (nextStep) {
    return { step: nextStep.id as OnboardingStep, substep: nextStep.substeps[0] };
  }

  // No next step
  return null;
};

export const getPreviousSubstep = (currentStep: OnboardingStep, currentSubstep: string) => {
  const stepIndex = ONBOARDING_STEPS.findIndex(s => s.id === currentStep);
  const stepConfig = ONBOARDING_STEPS[stepIndex];
  const substepIndex = (stepConfig.substeps as readonly string[]).indexOf(currentSubstep);

  // Previous substep in current step
  if (substepIndex > 0) {
    return { step: currentStep, substep: stepConfig.substeps[substepIndex - 1] };
  }

  // Last substep of previous step
  const prevStep = ONBOARDING_STEPS[stepIndex - 1];
  if (prevStep) {
    const lastSubstep = prevStep.substeps[prevStep.substeps.length - 1];
    return { step: prevStep.id as OnboardingStep, substep: lastSubstep };
  }

  // No previous step
  return null;
};

// Helper functions
export const getTargetPlatforms = (data: OnboardingData): Platform[] => {
  return data.platformTargets || [];
};

// Dynamic substep helpers
export const isDynamicTargetStep = (substep: string): boolean => {
  return substep.startsWith('connect_target_account_');
};

export const getTargetIndexFromSubstep = (substep: string): number => {
  if (isDynamicTargetStep(substep)) {
    const match = substep.match(/connect_target_account_(\d+)/);
    return match ? parseInt(match[1], 10) : 0;
  }
  return -1;
};

export const generateDynamicSubsteps = (stepId: string, data: OnboardingData): string[] => {
  const staticStep = ONBOARDING_STEPS.find(s => s.id === stepId);
  if (!staticStep) return [];

  if (stepId === 'accounts_workflows') {
    const substeps = [...staticStep.substeps] as string[];
    const platforms = getTargetPlatforms(data);

    if (platforms.length > 0) {
      // Find where to insert dynamic steps (after connect_trigger_account)
      const triggerIndex = substeps.indexOf('connect_trigger_account');

      if (triggerIndex !== -1) {
        // Insert dynamic target connection steps
        for (let i = 0; i < platforms.length; i++) {
          substeps.splice(triggerIndex + 1 + i, 0, `connect_target_account_${i}` as any);
        }
      }
    }

    // Find where to insert generate_workflow step (before ai_workflow_enhancements)
    const enhancementsIndex = substeps.indexOf('ai_workflow_enhancements');
    if (enhancementsIndex !== -1) {
      substeps.splice(enhancementsIndex, 0, 'generate_workflow' as any);
    }

    return substeps;
  }

  return [...staticStep.substeps] as string[];
};

// Validation helper with dynamic step support
export const isSubstepComplete = (substep: string, data: OnboardingData): boolean => {

  // Handle dynamic target connection steps
  if (isDynamicTargetStep(substep)) {
    const index = getTargetIndexFromSubstep(substep);
    const accounts = data.connectedTargetAccounts || [];
    const account = accounts[index];

    if (!account) return false;

    // Handle pending_selection status for related accounts
    if (account.status === 'pending_selection') {
      // Require related account selection to proceed
      return (account.relatedAccounts?.length || 0) > 0 &&
             account.selectedRelatedAccountId !== undefined;
    }

    // First target is required
    if (index === 0) {
      return account.status === 'connected';
    }

    // Additional targets are optional (can be skipped)
    return account.status === 'connected' || account.status === 'skipped';
  }

  // Handle trigger account connection with new schema
  if (substep === 'connect_trigger_account') {
    const trigger = data.triggerConnection;
    if (!trigger) return false;

    // Handle pending_selection status for related accounts
    if (trigger.status === 'pending_selection') {
      // Check if we have the new relatedAccounts field
      const relatedAccounts = (trigger as any).relatedAccounts;
      const selectedRelatedAccountId = (trigger as any).selectedRelatedAccountId;
      return (relatedAccounts?.length || 0) > 0 && selectedRelatedAccountId !== undefined;
    }

    const result = trigger.status === 'connected';
    if (!result) {
      console.log('[OAuth Debug] Trigger account not connected yet, status:', trigger.status);
    }
    return result;
  }

  // Handle generate_workflow substep
  if (substep === 'generate_workflow') {
    return data.workflowCreated === true && data.workflowId !== undefined;
  }

  // Handle static fields
  const fields = SUBSTEP_FIELDS[substep as keyof typeof SUBSTEP_FIELDS];
  if (!fields) return true;

  const fieldList = Array.isArray(fields) ? fields : [fields];
  return fieldList.every(field => {
    const value = data[field as keyof OnboardingData];
    if (Array.isArray(value)) return value.length > 0;
    if (typeof value === 'boolean') return value === true;
    if (typeof value === 'string' && value === 'connected') return true;

    // Handle new triggerConnection object
    if (field === 'triggerConnection' && typeof value === 'object') {
      const result = (value as any).status === 'connected';
      console.log('[OAuth Debug] generic triggerConnection path:', {
        field,
        value,
        status: (value as any).status,
        result
      });
      return result;
    }

    return value !== undefined && value !== null && value !== 'not started';
  });
};

// Check if all required targets are connected
export const areRequiredTargetsConnected = (data: OnboardingData): boolean => {
  const accounts = data.connectedTargetAccounts || [];

  // First target must be connected
  if (accounts.length === 0) return false;
  if (accounts[0]?.status !== 'connected') return false;

  // Other targets can be skipped
  return accounts.slice(1).every(
    account => account.status === 'connected' || account.status === 'skipped'
  );
};

// Helper function to format platform names for display
export const formatPlatform = (platform: Platform): string => {
  return PLATFORM_CONFIG[platform]?.name || platform;
};

// Helper function to get platform icon
export const getPlatformIcon = (platform: Platform): string => {
  return PLATFORM_CONFIG[platform]?.icon || 'mdi:help-circle';
};

