        />
      </button>
    </div>

    <!-- Content Area -->
    <div class="doc-sections mt-8">
      <template v-if="singleDoc">
        <!-- Single doc for modal -->
        <DocSection :doc="currentDoc" :all-docs="docs" />
      </template>
      <template v-else>
        <!-- All docs for full page with categories -->
        <div
          v-for="doc in filteredDocs"
          :key="doc.id"
          :id="doc.slug"
          class="doc-section mb-12"
        >
          <DocSection :doc="doc" :all-docs="docs" />
        </div>
      </template>
    </div>
  </div>
