---
import Navbar from "@vue/global/Navbar.vue";
import SideMenu from "@vue/global/SideMenu.vue";
import Footer from "@vue/global/Footer.vue";
import GlobalActionWidget from "@vue/global/GlobalActionWidget.vue";
import GlobalActionWidgetWithOverrides from "@vue/global/GlobalActionWidgetWithOverrides.vue";
import SmartNotifications from "@vue/global/SmartNotifications.vue";
import DataProvider from "@components/providers/DataProvider.vue";
import CreatePostProvider from "@vue/providers/CreatePostProvider.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import DocsModal from "@vue/modals/DocsModal.vue";
import { SITE_DATA } from "@/constants/siteData";
import { getCollection } from 'astro:content';
import { docCategories } from '@/content/docs/categories';

const siteData = SITE_DATA;

interface Props {
  title: string;
}

const sessionUser = await Astro.session?.get("authUser");

const { title } = Astro.props;

// Fetch docs for DocsModal
const allDocs = await getCollection('docs');

// Prevent caching of authenticated pages to avoid logout issues
Astro.response.headers.set('Cache-Control', 'private, no-cache, no-store, must-revalidate');
Astro.response.headers.set('Pragma', 'no-cache');
Astro.response.headers.set('Expires', '0');
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />
    <style is:global>
      html {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
        font-family: system-ui, sans-serif;
      }

      html,
      body,
      #body {
        height: 100% !important;
      }

      [data-theme="dark"] {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body
    id="body"
    class="relative min-h-screen flex flex-col overflow-hidden touch-optimized mobile-a11y"
  >
    <Navbar client:load transition:persist user={sessionUser} />
    <div class="flex-1 flex overflow-hidden">
      <SideMenu client:load />
      <div
        class="flex-1 flex flex-col overflow-y-auto bg-gray-100 dark:bg-gray-900 transition-colors duration-300"
      >
        <DataProvider client:load>
          <slot />
        </DataProvider>
        <Footer client:load transition:persist />
      </div>
    </div>

    <CookieGuard client:load transition:persist />
    <CreatePostProvider client:load />
    <SessionInitializer client:load />

    <!-- Teleport Layer: keep this as a single top-level target for modals/popovers -->
    <div id="teleport-layer"></div>

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: ["class", '[data-theme="dark"]'],
  content: ["./src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}"],
  theme: {
    extend: {
      colors: {
        primary: {
          100: "#cce5e7",
          200: "#99cbd0",
          300: "#66b0b8",
          400: "#3396a1",
          500: "#007c89",
          600: "#00636e",
          700: "#004a52",
          800: "#003237",
          900: "#00191b",
        },

        // Accent colors from research
        accent: {
          purple: {
            50: '#faf5ff',
            100: '#f3e8ff',
            200: '#e9d5ff',
            300: '#d8b4fe',
            400: '#c084fc',
            500: '#7c3aed',  // AI features
            600: '#6d28d9',
            700: '#5b21b6',
            800: '#4c1d95',
            900: '#3b0764',
          },
          orange: {
            50: '#fff7ed',
            100: '#ffedd5',
            200: '#fed7aa',
            300: '#fdba74',
            400: '#fb923c',
            500: '#f59e0b',  // CTAs/highlights
            600: '#d97706',
            700: '#b45309',
            800: '#92400e',
            900: '#78350f',
          },
        },

        // Dark mode optimized colors
        dark: {
          bg: {
            primary: '#0D1117',     // Near-black (not pure black)
            secondary: '#161B22',
            tertiary: '#1F2937',
          },
          border: '#30363d',
          text: {
            primary: '#f9fafb',
            secondary: '#d1d5db',
            tertiary: '#9ca3af',
          },
        },
      },

      // Fluid typography with clamp()
      fontSize: {
        'display-2xl': 'clamp(3rem, 5vw, 4.5rem)',    // 48-72px hero
        'display-xl': 'clamp(2.5rem, 4vw, 3.5rem)',   // 40-56px
        '3xl': 'clamp(1.875rem, 3vw, 2.25rem)',       // 30-36px
      },

      // Letter spacing for headlines
      letterSpacing: {
        tighter: '-0.05em',
        tight: '-0.025em',
      },

      // Animation durations - synced with src/constants/animations.ts
      transitionDuration: {
        'instant': '100ms',  // Quick feedback
        'fast': '200ms',     // Hover states, simple selections
        'normal': '300ms',   // Standard transitions
        'slow': '500ms',     // Page transitions (max)
        'celebration': '2000ms' // Success celebrations
      },

      // Animation timing functions - synced with src/constants/animations.ts
      transitionTimingFunction: {
        'ease-out-custom': 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'ease-in-out-custom': 'cubic-bezier(0.42, 0, 0.58, 1)',
        'bounce-custom': 'cubic-bezier(0.34, 1.56, 0.64, 1)',
        'smooth': 'cubic-bezier(0.16, 1, 0.3, 1)',
        'sharp': 'cubic-bezier(0.4, 0, 0.2, 1)',
        'elastic': 'cubic-bezier(0.68, -0.55, 0.265, 1.55)'
      },

      // Minimal utility animations (for static decorative elements only)
      // Use useAnimateElement composable for all interactive/controlled animations
      animation: {
        'fade-in': 'fadeIn 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'slide-in-up': 'slideInUp 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'pulse-slow': 'pulse 3s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'spin-slow': 'spin 8s linear infinite',
        'float-slow': 'floatSlow 20s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'shimmer': 'shimmer 2s linear infinite',
      },

      keyframes: {
        fadeIn: {
          '0%': { opacity: '0' },
          '100%': { opacity: '1' }
        },
        slideInUp: {
          '0%': { opacity: '0', transform: 'translateY(20px)' },
          '100%': { opacity: '1', transform: 'translateY(0)' }
        },
        floatSlow: {
          '0%, 100%': { transform: 'translate(0, 0)' },
          '50%': { transform: 'translate(20px, -20px)' }
        },
        shimmer: {
          '0%': { transform: 'translateX(-100%)' },
          '100%': { transform: 'translateX(100%)' }
        }
      }
    },
  },
  plugins: [
    require('@tailwindcss/typography'),
  ],
};

