  --spacing-24: var(--spacing-24);
  --spacing-28: var(--spacing-28);
  --spacing-32: var(--spacing-32);

  /* Border radius */
  --radius-none: var(--radius-none);
  --radius-sm: var(--radius-sm);
  --radius-base: var(--radius-base);
  --radius-md: var(--radius-md);
  --radius-lg: var(--radius-lg);
  --radius-xl: var(--radius-xl);
  --radius-2xl: var(--radius-2xl);
  --radius-3xl: var(--radius-3xl);
  --radius-full: var(--radius-full);
}

.label-position {
  top: min(min(-1.5vh, -1.5vw), -1.5rem);
}

/* Prose styles for documentation */
.prose {
  @apply text-gray-900;
}

.prose h1,
.prose h2,
.prose h3,
.prose h4,
.prose h5,
.prose h6 {
  @apply text-gray-900;
}

.prose a {
  @apply text-primary-600 hover:text-primary-700 transition-colors;
}

.prose code {
  @apply bg-gray-100 text-gray-900 px-1 py-0.5 rounded;
}

.prose pre {
  @apply bg-gray-100;
}

.prose strong {
  @apply text-gray-900;
}

.prose blockquote {
  @apply border-l-primary-500 text-gray-700;
}

/* Prose dark mode styles */
.dark .prose {
  @apply text-gray-100;
}

.dark .prose h1,
.dark .prose h2,
.dark .prose h3,
.dark .prose h4,
.dark .prose h5,
.dark .prose h6 {
  @apply text-white;
}

.dark .prose a {
  @apply text-primary-400 hover:text-primary-500;
}

.dark .prose code {
  @apply bg-gray-800 text-gray-100;
}

.dark .prose pre {
  @apply bg-gray-800;
}

.dark .prose strong {
  @apply text-gray-100;
}

.dark .prose blockquote {
  @apply border-l-primary-400 text-gray-300;
}

