// Configure marked with custom renderer for security
marked.use({
  headerIds: true,  // Enable automatic heading IDs for TOC
  mangle: false,    // Don't mangle IDs (keep them readable)
  breaks: true,     // GitHub-flavored line breaks
  renderer: {
    link(href, title, text) {
      // Add security attributes to external links
      const hrefStr = String(href || '');
      const titleStr = title ? String(title) : '';
      const textStr = String(text || '');

      if (hrefStr && /^https?:\/\//i.test(hrefStr)) {
        return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''} target="_blank" rel="noopener noreferrer">${textStr}</a>`;
      }
      return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''}>${textStr}</a>`;
    }
  }
});

