const computedStyles = computed((): CSSProperties => ({
  zIndex: props.zIndex,
  opacity: (!isMounted.value || (!props.modelValue && !isVisible.value)) ? 0 : 1,
  visibility: (!isMounted.value || (!props.modelValue && !isVisible.value)) ? 'hidden' : 'visible',
  position: 'absolute', // default to absolute positioning for teleported content
  inset: 0, // fill the teleported container
  ...props.positioning // allow override via positioning prop
}))

const containerClasses = computed(() => [
  'animate-floating-element',
  props.containerClass
])

const shouldRender = computed(() => isMounted.value && (props.modelValue || isVisible.value || isAnimating.value))
