const addPlatform = (platform: string) => {
  const currentTargets = [...(data.value?.platformTargets || [])];
  currentTargets.push(platform as any);

  // Initialize connectedTargetAccounts array to match platforms
  const connectedAccounts = [...(data.value?.connectedTargetAccounts || [])];
  connectedAccounts.push({
    platform: platform as any,
    status: 'not started' as any
  });

  // Check if any target platform requires related account selection
  const requiresRelated = [...currentTargets].some(p => ['youtube', 'facebook_pages'].includes(p));

  update({
    platformTargets: currentTargets,
    connectedTargetAccounts: connectedAccounts,
    requiresRelatedSelection: requiresRelated
  });
};

const removePlatform = (platform: string, index: number) => {
  const currentTargets = [...(data.value?.platformTargets || [])];
  currentTargets.splice(index, 1);

  // Remove corresponding connected account
  const connectedAccounts = [...(data.value?.connectedTargetAccounts || [])];
  connectedAccounts.splice(index, 1);

  // Check if any remaining platforms require related account selection
  const triggerRequiresRelated = data.value?.platformTrigger && ['youtube', 'facebook_pages'].includes(data.value.platformTrigger);
  const targetRequiresRelated = currentTargets.some(p => ['youtube', 'facebook_pages'].includes(p));
  const requiresRelated = triggerRequiresRelated || targetRequiresRelated;

  update({
    platformTargets: currentTargets,
    connectedTargetAccounts: connectedAccounts,
    requiresRelatedSelection: requiresRelated
  });
};

const getPlatformCount = (platform: string): number => {
  return (data.value?.platformTargets || []).filter(p => p === platform).length;
};

const applyRecommendation = (pair: string[]) => {
  // Check if any platform in the recommended flow requires related account selection
  const requiresRelated = ['youtube', 'facebook_pages'].some(p =>
    pair.includes(p)
  );

  // Clear existing targets and set recommended flow
  update({
    platformTrigger: pair[0] as any,
    platformTargets: [pair[1]] as any,
    connectedTargetAccounts: [{
      platform: pair[1] as any,
      status: 'not started' as any
    }],
    requiresRelatedSelection: requiresRelated
  });
};

const formatPlatform = (value: string) => {
  return value ? value.charAt(0).toUpperCase() + value.slice(1) : '';
};

const getPlatformIcon = (value: string) => {
  const platform = availablePlatforms.find(p => p.value === value);
  return platform?.icon || 'mdi:help-circle';
};

defineEmits<{
  next: [];
  previous: [];
  update: [updates: any];
}>();

</script>

