const { dimensions } = animation.responsive!

// Base values from design tokens - children will handle responsive scaling
const baseFlyDistance = ANIMATION_TOKENS.theaters.workflow.flyDistance
const baseCardWidth = ANIMATION_TOKENS.theaters.workflow.cardWidth
const DUPLICATE_CARD_BASE = 100 // Base size for flying duplicate cards

// Calculate target positions - pass base values, children scale based on their containers
const getTargetX = (index: number) => {
  return `${baseFlyDistance}px`
}

const getTargetY = (index: number) => {
  const row = Math.floor(index / 2)
