     1→<template>
     2→  <article v-if="doc" class="doc-section-content">
     3→    <h2 class="text-2xl font-bold mb-3 text-gray-900 dark:text-white">
     4→      {{ doc.data.title }}
     5→    </h2>
     6→    <p class="text-gray-600 dark:text-gray-400 mb-6">
     7→      {{ doc.data.description }}
     8→    </p>
     9→    <div
    10→      class="prose prose-gray dark:prose-invert max-w-none"
    11→      v-html="renderedContent"
    12→    />
    13→
    14→    <!-- Related Sections -->
    15→    <div v-if="doc.data.relatedSections?.length" class="mt-8 pt-6 border-t border-gray-200 dark:border-gray-700">
    16→      <h4 class="text-sm font-semibold text-gray-900 dark:text-white mb-3">Related Topics</h4>
    17→      <div class="flex flex-wrap gap-2">
    18→        <button
    19→          v-for="relatedSlug in doc.data.relatedSections"
    20→          :key="relatedSlug"
    21→          @click="navigateToRelated(relatedSlug)"
    22→          class="text-sm px-3 py-1 rounded-full bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors cursor-pointer"
    23→        >
    24→          {{ getRelatedTitle(relatedSlug) }}
    25→        </button>
    26→      </div>
    27→    </div>
    28→
    29→    <!-- Last Updated -->
    30→    <div v-if="doc.data.lastUpdated" class="mt-6 text-sm text-gray-500 dark:text-gray-400 border-t border-gray-200 dark:border-gray-700 pt-6">
    31→      Last updated: {{ new Date(doc.data.lastUpdated).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }) }}
    32→    </div>
    33→  </article>
    34→</template>
    35→
    36→<script setup lang="ts">
    37→import { computed, onMounted, ref } from 'vue';
    38→import type { CollectionEntry } from 'astro:content';
    39→import { marked } from 'marked';
    40→import { openDocsModal } from '@/stores/docsStore';
    41→
    42→// DOMPurify must be imported dynamically for SSR compatibility
    43→let DOMPurify: any = null;
    44→if (typeof window !== 'undefined') {
    45→  import('dompurify').then(module => {
    46→    DOMPurify = module.default;
    47→  });
    48→}
    49→
    50→const props = defineProps<{
    51→  doc: CollectionEntry<'docs'> | null;
    52→  allDocs: CollectionEntry<'docs'>[];
    53→}>();
    54→
    55→// Configure marked with custom renderer for security
    56→marked.use({
    57→  headerIds: true,  // Enable automatic heading IDs for TOC
    58→  mangle: false,    // Don't mangle IDs (keep them readable)
    59→  breaks: true,     // GitHub-flavored line breaks
    60→  renderer: {
    61→    link(href, title, text) {
    62→      // Add security attributes to external links
    63→      const hrefStr = String(href || '');
    64→      const titleStr = title ? String(title) : '';
    65→      const textStr = String(text || '');
    66→
    67→      if (hrefStr && /^https?:\/\//i.test(hrefStr)) {
    68→        return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''} target="_blank" rel="noopener noreferrer">${textStr}</a>`;
    69→      }
    70→      return `<a href="${hrefStr}"${titleStr ? ` title="${titleStr}"` : ''}>${textStr}</a>`;
    71→    }
    72→  }
    73→});
    74→
    75→const renderedContent = computed(() => {
    76→  if (!props.doc?.body) return '';
    77→  const html = marked(props.doc.body) as string;
    78→
    79→  // Only sanitize on client side (DOMPurify doesn't work in SSR)
    80→  if (typeof window !== 'undefined' && DOMPurify) {
    81→    return DOMPurify.sanitize(html, {
    82→      ALLOWED_TAGS: ['h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'p', 'a', 'ul', 'ol', 'li', 'code', 'pre', 'strong', 'em', 'blockquote', 'br', 'hr', 'table', 'thead', 'tbody', 'tr', 'th', 'td'],
    83→      ALLOWED_ATTR: ['href', 'class', 'id', 'target', 'rel']
    84→    });
    85→  }
    86→
    87→  // During SSR, return the marked HTML (it will be sanitized on client hydration)
    88→  return html;
    89→});
    90→
    91→const getRelatedTitle = (slug: string) => {
    92→  const relatedDoc = props.allDocs.find(d => d.slug === slug);
    93→  return relatedDoc?.data.title || slug.split('/').pop()?.replace(/-/g, ' ') || '';
    94→};
    95→
    96→const navigateToRelated = (slug: string) => {
    97→  // Check if we're in a modal context by checking window location
    98→  if (window.location.pathname !== '/docs') {
    99→    // In modal - open the related doc
   100→    openDocsModal(slug);
   101→  } else {
   102→    // On full page - use hash navigation
   103→    window.location.hash = slug;
   104→  }
   105→};
   106→</script>
   107→

