    79→<script setup lang="ts">
    80→import { onMounted, onUnmounted, nextTick } from "vue";
    81→import Typed from "typed.js";
    82→import { Icon } from "@iconify/vue";
    83→import AiConductorGraphicV2 from "../../ui/AiConductorGraphicV2.vue";
    84→import AiConductorGraphic from "../../ui/AiConductorGraphic.vue";
    85→import { usePerformance } from "@/components/composables/usePerformance";
    86→
    87→
    88→
    89→
    90→// Performance monitoring with all VueUse composables included
    91→const {
    92→  measureOperation,
    93→  optimizations,
    94→  batteryInfo,
    95→  networkInfo,
    96→  windowRef
    97→} = usePerformance();
    98→
    99→const typewriterStrings: string[] = ['Post once —', 'Schedule smarter —', 'Grow faster —'];
   100→
   101→let typedInstance: any = null;
   102→let cleanupRefs: {
   103→  timeouts: Set<ReturnType<typeof setTimeout>>;
   104→  callbacks: Set<number>;
   105→} = {
   106→  timeouts: new Set(),
   107→  callbacks: new Set()
   108→};
   109→
   110→onMounted(() => {
   111→  const initOperation = measureOperation('chatInitTime')
   112→
   113→  try {
   114→    // Check performance conditions for adaptive animations
   115→    const prefersReducedMotion = optimizations.reducedMotion
   116→
   117→    const lowBattery = optimizations.batteryOptimization
   118→
   119→    const slowNetwork = networkInfo.effectiveType &&
   120→      (networkInfo.effectiveType === 'slow-2g' ||
   121→       networkInfo.effectiveType === '2g')
   122→
   123→    const shouldOptimizeAnimations = prefersReducedMotion ||
   124→      (optimizations.batteryOptimization && lowBattery) ||
   125→      slowNetwork
   126→
   127→    // Adaptive typewriter configuration
   128→    const typewriterConfig = {
   129→      strings: typewriterStrings,
   130→      smartBackspace: false,
   131→      shuffle: !shouldOptimizeAnimations, // Disable shuffle for better performance
   132→      typeSpeed: shouldOptimizeAnimations ? 100 : 50, // Faster typing when optimizing
   133→      backSpeed: shouldOptimizeAnimations ? 60 : 30,
   134→      backDelay: shouldOptimizeAnimations ? 1000 : 2000, // Shorter delays
   135→      loop: !shouldOptimizeAnimations, // Disable loop for reduced motion
   136→      showCursor: true,
   137→      cursorChar: '|',
   138→    };
   139→
   140→    // Add progressive enhancement classes
   141→    if (windowRef.value) {
   142→      windowRef.value.document.documentElement.classList.add('js-enabled')
   143→      if (shouldOptimizeAnimations) {
   144→        windowRef.value.document.documentElement.classList.add('reduced-motion')
   145→      }
   146→    }
   147→
   148→    // Initialize typewriter with performance considerations
   149→    if (!shouldOptimizeAnimations) {
   150→      typedInstance = new Typed(".typewriter", typewriterConfig);
   151→    } else {
   152→      // For reduced performance scenarios, show static text
   153→      if (windowRef.value) {
   154→        const typewriterElement = windowRef.value.document.querySelector('.typewriter');
   155→        if (typewriterElement) {
   156→          typewriterElement.textContent = typewriterStrings[0].replace(' —', '');
   157→        }
   158→      }
   159→    }
   160→
   161→    // Use requestIdleCallback for non-critical operations
   162→    nextTick(() => {
   163→      if (windowRef.value && 'requestIdleCallback' in windowRef.value && !shouldOptimizeAnimations) {
   164→        const callbackId = (windowRef.value as any).requestIdleCallback(() => {
   165→          cleanupRefs.callbacks.delete(callbackId)
   166→          // Non-critical initialization code
   167→        });
   168→        cleanupRefs.callbacks.add(callbackId)
   169→      } else {
   170→        // Fallback for browsers without requestIdleCallback or when optimizing
   171→        const timeoutId = setTimeout(() => {
   172→          cleanupRefs.timeouts.delete(timeoutId)
   173→          // Simplified initialization
   174→        }, shouldOptimizeAnimations ? 0 : 100);
   175→        cleanupRefs.timeouts.add(timeoutId)
   176→      }
   177→    });
   178→
   179→    initOperation.end()
   180→  } catch (error) {
   181→    console.error('HeroSection: Error during typewriter initialization:', error)
   182→    initOperation.end()
   183→  }
   184→});
   185→
   186→onUnmounted(() => {
   187→  // Clean up typewriter instance
   188→  if (typedInstance) {
   189→    typedInstance.destroy();
   190→    typedInstance = null;
   191→  }
   192→
   193→  // Clear all timeouts
   194→  cleanupRefs.timeouts.forEach(timeout => {
   195→    clearTimeout(timeout);
   196→  });
   197→  cleanupRefs.timeouts.clear();
   198→
   199→  // Cancel idle callbacks
   200→  cleanupRefs.callbacks.forEach(callbackId => {
   201→    if (windowRef.value && 'cancelIdleCallback' in windowRef.value) {
   202→      (windowRef.value as any).cancelIdleCallback(callbackId);
   203→    }
   204→  });
   205→  cleanupRefs.callbacks.clear();
   206→});
   207→</script>
