// Animation handling for testimonials
const testimonialRefs = ref<HTMLElement[]>([]);

// Initialize hasBeenVisible array
const hasBeenVisible = ref<boolean[]>(Array(testimonials.value.length).fill(false));

const isVisible = computed(() => {
  if (!mounted.value || !testimonials.value?.length) return [];
  return testimonials.value.map((_, index) => {
    const target = () => testimonialRefs.value[index];
    return useElementVisibility(target, { threshold: 0.2 });
  });
});

// Watch for visibility changes and mark items as "seen" permanently
watch(isVisible, (newValues) => {
  newValues.forEach((visibilityRef, index) => {
    if (visibilityRef?.value === true) {
      hasBeenVisible.value[index] = true;
    }
  });
}, { deep: true });

